insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (87,'KHH12A','Cấp Giấy chứng nhận đăng ký kinh doanh cho hộ cá thể',15,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.1. Trình tự thực\r\nhiện: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n1:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> Cá nhân<span style=\"letter-spacing:-.2pt\"> nộp hồ sơ tại Bộ phận tiếp nhận và trả kết quả\r\ncủa UBND huyện, thành phố; cán bộ tiếp nhận hướng dẫn viết giấy đề nghị đăng ký\r\nkinh doanh, kiểm tra hồ sơ và viết giấy hẹn.</span><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n2:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; Hồ sơ được chuyển tới phòng Tài chính - Kế\r\nhoạch để thẩm định và hoàn thiện giấy chứng nhận đăng ký kinh doanh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.3pt\">Bước 3:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.3pt\"> Phòng tài chính - Kế hoạch trình lãnh đạo UBND huyện xem\r\nxét và ký giấy chứng nhận đăng ký kinh doanh; Hồ sơ được chuyển lại một cửa của\r\nUBND huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n4:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> Cá nhân xuất\r\ntrình giấy hẹn trả kết quả, nộp lệ phí và lấy Giấy CN kinh doanh tại bộ phận\r\ntiếp nhận và trả kết quả của UBND&nbsp; huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.2. Cách thức\r\nthực hiện:&nbsp; </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tại Bộ phận tiếp nhận và trả kết\r\nquả<span style=\"letter-spacing:-.2pt\"> UBND huyện</span><b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.3. Hồ sơ:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a) Thành phần hồ sơ bao gồm:<o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1- Giấy đề nghị đăng ký kinh\r\ndoanh;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2- Bản sao Chứng minh thư nhân\r\ndân;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">3- Bản sao hợp lệ chứng chỉ hành\r\nnghề (đối với ngành nghề đủ điều kiện phải có chứng chỉ hành nghề).<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">b) Số lượng hồ sơ</span></i></b><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;\r\n01 (bộ)<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;letter-spacing:-.5pt\">15.4.\r\nThời hạn giải quyết:</span></b><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;\r\n</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">05 ngày\r\nlàm việc kể từ ngày nhận đủ hồ sơ hợp lệ<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.5. Cơ quan thực\r\nhiện:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a) Cơ quan có thẩm quyền quyết\r\nđịnh: Chủ tịch UBND huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">b<span style=\"letter-spacing:\r\n-.3pt\">) Cơ quan trực tiếp thực hiện: Phòng Tài chính – Kế hoạch.</span><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;letter-spacing:-.5pt\">15.6.\r\nĐối tượng thực hiện:&nbsp; </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Cá nhân&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<b><span style=\"letter-spacing:-.5pt\"><o:p></o:p></span></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.7. Mẫu đơn,&nbsp;&nbsp; tờ khai:&nbsp;\r\n</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.2pt\">Giấy đề nghị đăng ký kinh doanh hộ kinh doanh <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">(Thông tư số 01/2013/TT-BKH ngày\r\n21/1/2013 của Bộ Kế hoạch và Đầu tư) <b><o:p></o:p></b></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.8. Phí, lệ phí: </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Lệ phí đăng ký kinh doanh:\r\n100.000 đồng<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">( Thông tư số 176/2012/TT-BTC\r\nngày 23/12/2012 của Bộ Tài chính)<b><o:p></o:p></b></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.9. Kết quả: </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-bidi-font-weight:bold\">Giấy\r\nchứng nhận<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.10.\r\nYêu cầu, điều kiện:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></b><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">15.11. Căn cứ pháp\r\nlý:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Luật Doanh nghiệp số\r\n60/2005/QH11 ngày 29/11/2005<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Nghị định 43/2010/NĐ-CP ban\r\nhành ngày 15/4/2010 của Chính phủ&nbsp; về\r\nđăng ký doanh nghiệp;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-Thông tư số 176/2012/TT-BTC ngày\r\n23/12/2012 của Bộ Tài chính quy định mức thu, chế độ thu, nộp, quản lý và sử\r\ndụng lệ phí đăng ký doanh nghiệp, đăng ký hộ kinh doanh và phí cung cấp thông\r\ntin doanh nghiệp<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Nghị định 05/2013/NĐ-CP ban\r\nhành ngày 09/01/2013&nbsp; của Chính phủ&nbsp; hướng dẫn sửa đổi, bổ sung một số điều quy\r\nđịnh về thủ tục hành chính của Nghị định 43/2010/NĐ-CP ban hành ngày 15/4/2010\r\ncủa Chính phủ&nbsp; về đăng ký doanh nghiệp<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-Thông tư số 01/2013/TT-BKH ngày\r\n21/1/2013 của Bộ Kế hoạch và Đầu tư hướng dẫn về hướng dẫn đăng ký doanh nghiệp<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Nghị quyết số 11/2007/NQ-HĐND\r\nngày 19/7/2007 của Hội đồng nhân dân tỉnh Bắc Giang về việc Quy định mức thu và\r\ntỷ lệ điều tiết các loại phí, lệ phí của tỉnh Bắc Giang.<o:p></o:p></span></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (97,'LDH18A','Tiếp nhận đối tượng hưởng trợ cấp xã hội, hỗ trợ kinh phí chăm sóc người khuyết tật khi đối tượng thay đổi nơi cư trú',16,NULL,3,'<p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b></b><b><span lang=\"VI\">Trình tự thực hiện:<o:p></o:p></span></b></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"VI\">Bước 1:</span></b><span lang=\"VI\"> </span>Cá nhân chuẩn bị hồ sơ nộp tại Bộ phận Tiếp nhận và trả kết quả-\r\nUBND cấp huyện nơi cư trú mới<o:p></o:p></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"VI\">Bước 2:</span></b><span lang=\"VI\"> Trong thời hạn 07 ngày làm việc, kể từ\r\nngày nhận đủ hồ sơ </span>hợp lệ<span lang=\"VI\">,\r\nPhòng Lao động - Thương binh và Xã hội nơi cư trú mới có trách nhiệm xem xét và\r\ntrình Chủ tịch Ủy ban nhân dân cấp huyện quyết định hưởng trợ cấp xã hội, hỗ\r\ntrợ kinh phí chăm sóc theo mức tương ứng của địa phương;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"VI\">Thời gian hưởng trợ cấp xã hội, hỗ trợ kinh phí\r\nchăm sóc tại nơi ở mới từ tháng tiếp theo của tháng thôi hưởng trợ cấp, hỗ trợ\r\nkinh phí chăm sóc ghi trong quyết định thôi hưởng trợ cấp xã hội, hỗ trợ kinh\r\nphí chăm sóc của Chủ tịch Ủy ban nhân dân cấp huyện nơi ở cũ của đối tượng.<o:p></o:p></span></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b></b><b><span lang=\"VI\">Cách thức thực hiện: </span><o:p></o:p></b></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">Trực tiếp tại Bộ phận\r\nTiếp nhận và trả kết quả- UBND cấp huyện<o:p></o:p></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span style=\"letter-spacing:-.3pt\"></span></b><b><span lang=\"VI\" style=\"letter-spacing:\r\n-.3pt;mso-ansi-language:VI\">Thành phần, số lượng hồ sơ:<o:p></o:p></span></b></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>a) Thành phần hồ sơ:<o:p></o:p></i></b></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">1. Quyết định thôi hưởng trợ cấp xã hội, hỗ trợ kinh phí chăm sóc;<o:p></o:p></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">2. Giấy giới thiệu của UBND cấp huyện nơi chuyển đi;<o:p></o:p></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">3. Hồ sơ đang hưởng trợ cấp xã hội đối với người khuyết tật<o:p></o:p></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>b) </i></b><b><i><span lang=\"VI\">Số lượng hồ sơ:</span></i></b><span lang=\"VI\"> 01 (bộ)<b><o:p></o:p></b></span></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b></b><b><span lang=\"VI\">Thời hạn giải quyết: </span></b><span lang=\"VI\">07 ngày làm việc kể từ khi nhận đầy đủ hồ\r\nsơ hợp lệ.<o:p></o:p></span></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b></b><b><span lang=\"VI\">Đối tượng thực hiện: </span></b><span lang=\"VI\">Cá nhân.<b><o:p></o:p></b></span></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cơ quan thực\r\nhiện: <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Cơ quan có thẩm\r\nquyền quyết định: Chủ tịch UBND huyện.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Cơ quan trực\r\ntiếp thực hiện: Phòng Lao động - Thương binh và xã hội.<o:p></o:p></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b> Kết quả: </b>Quyết\r\nđịnh hành chính.<o:p></o:p></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b> Phí, lệ phí: </b>Không.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Tên mẫu\r\nđơn, mẫu tờ khai: </b>Không<o:p></o:p></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Yêu cầu, điều\r\nkiện: </b>Không.<b><o:p></o:p></b></p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Căn cứ pháp\r\nlý:<o:p></o:p></b></p><p class=\"p15\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;\r\nmargin-left:0in;margin-bottom:.0001pt;text-align:justify\">- Luật Người khuyết tật ngày 17/6/2010;<o:p></o:p></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"p15\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;\r\nmargin-left:0in;margin-bottom:.0001pt;text-align:justify\">- Nghị định số 28/2012/NĐ-CP ngày 10/4/2012\r\ncủa Chính phủ quy định chi tiết và hướng dẫn thi hành một số điều của Luật\r\nngười khuyết tật;<o:p></o:p></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (98,'LDH25A','Quyết định áp dụng biện pháp quản lý sau cai nghiện tại nơi cư trú',16,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Trình tự thực hiện: <o:p></o:p></b></p><p style=\"margin-bottom:6.0pt;text-align:justify\"><b>Bước 1:</b> Trước khi kết thúc thời gian cai nghiện bắt buộc bốn mươi\r\nlăm ngày, Giám đốc Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội có trách\r\nnhiệm lập hồ sơ đề nghị áp dụng biện pháp quản lý sau cai nghiện tại nơi cư\r\ntrú.<o:p></o:p></p><p style=\"margin-bottom:6.0pt;text-align:justify\"><b>Bước 2:</b> Giám đốc Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội\r\ncó trách nhiệm chuyển hồ sơ cho Trưởng Phòng Lao động – Thương binh và Xã hội\r\ncấp huyện nơi lập hồ sơ đưa đối tượng vào Trung tâm Chữa bệnh – Giáo dục – Lao\r\nđộng xã hội chậm nhất là hai mươi ngày trước khi người đó kết thúc thời gian\r\ncai nghiện bắt buộc.<o:p></o:p></p><p style=\"margin-bottom:6.0pt;text-align:justify\"><b>Bước 3:</b> Trong thời hạn năm ngày, kể từ ngày nhận được hồ sơ, Trưởng\r\nphòng Lao động – Thương binh và Xã hội có trách nhiệm thẩm tra và làm văn bản\r\ntrình Chủ tịch Ủy ban nhân dân cấp huyện.<o:p></o:p></p><p style=\"margin-bottom:6.0pt;text-align:justify\">Bước 4: Trong thời hạn năm\r\nngày, kể từ ngày nhận được văn bản của Trưởng Phòng Lao động – Thương binh và\r\nXã hội, Chủ tịch Ủy ban nhân dân cấp huyện quyết định áp dụng biện pháp quản lý\r\nsau cai nghiện tại nơi cư trú và thông báo danh sách đối tượng thuộc diện quản\r\nlý sau cai nghiện cho Ủy ban nhân dân cấp xã nơi người nghiện cư trú để có kế\r\nhoạch tiếp nhận và thực hiện việc quản lý sau cai nghiện. <o:p></o:p></p><p style=\"margin-bottom:6.0pt;text-align:justify\">Quyết định phải gửi cho người\r\ncai nghiện, gia đình người đó, Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội\r\nnơi đang quản lý người cai nghiện và Ủy ban nhân dân cấp xã nơi người đó cư\r\ntrú.<o:p></o:p></p><p class=\"bodytext1\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cách thức thực hiện:<o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Trực tiếp tại Bộ\r\nphận Tiếp nhận và trả kết quả- UBND cấp huyện<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Thành phần, số lượng hồ sơ:<o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone\"><b><i>a) Thành phần hồ sơ:<o:p></o:p></i></b></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify;mso-pagination:none\">1. Bản sao hồ sơ\r\ncủa đối tượng cai nghiện bắt buộc tại Trung tâm Chữa bệnh – Giáo dục – Lao động\r\nxã hội gồm:<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;text-autospace:none\"><span style=\"letter-spacing:.05pt\">-</span><span style=\"letter-spacing:.35pt\"> </span>Hồ<span style=\"letter-spacing:.4pt\"> </span>sơ<span style=\"letter-spacing:.45pt\"> </span><span style=\"letter-spacing:-.05pt\">đ</span>ề<span style=\"letter-spacing:.45pt\"> </span>ng<span style=\"letter-spacing:-.05pt\">h</span>ị<span style=\"letter-spacing:.5pt\"> </span><span style=\"letter-spacing:-.05pt\">á</span>p<span style=\"letter-spacing:.4pt\"> </span><span style=\"letter-spacing:-.05pt\">d</span>ụ<span style=\"letter-spacing:.05pt\">n</span>g<span style=\"letter-spacing:.4pt\"> </span><span style=\"letter-spacing:.05pt\">b</span>i<span style=\"letter-spacing:-.05pt\">ệ</span>n<span style=\"letter-spacing:.4pt\"> </span>pháp<span style=\"letter-spacing:.4pt\"> </span><span style=\"letter-spacing:.05pt\">đ</span><span style=\"letter-spacing:-.05pt\">ư</span>a<span style=\"letter-spacing:.4pt\"> </span>v<span style=\"letter-spacing:-.05pt\">à</span>o<span style=\"letter-spacing:.5pt\"> </span>cơ<span style=\"letter-spacing:.35pt\"> </span><span style=\"letter-spacing:.05pt\">s</span>ở<span style=\"letter-spacing:.35pt\"> </span>ch<span style=\"letter-spacing:-.05pt\">ữ</span>a<span style=\"letter-spacing:.5pt\"> </span>b<span style=\"letter-spacing:-.05pt\">ệ</span><span style=\"letter-spacing:.05pt\">n</span>h<span style=\"letter-spacing:.35pt\"> </span><span style=\"letter-spacing:.05pt\">t</span><span style=\"letter-spacing:-.05pt\">h</span>eo<span style=\"letter-spacing:.4pt\"> </span><span style=\"letter-spacing:.05pt\">q</span><span style=\"letter-spacing:-.05pt\">u</span>y<span style=\"letter-spacing:.4pt\"> </span>định<span style=\"letter-spacing:.45pt\"> </span><span style=\"letter-spacing:-.05pt\">t</span>ại Điều<span style=\"letter-spacing:.05pt\">\r\n</span>9<span style=\"letter-spacing:.05pt\"> </span>Ng<span style=\"letter-spacing:\r\n.05pt\">h</span>ị<span style=\"letter-spacing:.1pt\"> </span><span style=\"letter-spacing:-.05pt\">đ</span>ịnh<span style=\"letter-spacing:.05pt\"> </span>135/2004/<span style=\"letter-spacing:-.1pt\">N</span><span style=\"letter-spacing:-.05pt\">Đ</span><span style=\"letter-spacing:.05pt\">-C</span>P <span style=\"letter-spacing:.05pt\">h</span>oặc\r\nĐ<span style=\"letter-spacing:.05pt\">i</span><span style=\"letter-spacing:-.05pt\">ề</span>u<span style=\"letter-spacing:.05pt\"> </span>16<span style=\"letter-spacing:.05pt\"> </span>Nghị\r\n<span style=\"letter-spacing:.05pt\">đ</span><span style=\"letter-spacing:-.05pt\">ị</span>nh\r\n43/2005/<span style=\"letter-spacing:-.05pt\">N</span>Đ-CP;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;text-autospace:none\"><span style=\"letter-spacing:.05pt\">-</span> <span style=\"letter-spacing:.05pt\">Q</span><span style=\"letter-spacing:-.05pt\">u</span>yết\r\n<span style=\"letter-spacing:.05pt\">đ</span>ịnh việc đ<span style=\"letter-spacing:\r\n.05pt\">ư</span>a <span style=\"letter-spacing:.05pt\">v</span>ào cơ sở <span style=\"letter-spacing:-.05pt\">c</span>h<span style=\"letter-spacing:.05pt\">ữ</span>a\r\nbệ<span style=\"letter-spacing:-.05pt\">n</span>h <span style=\"letter-spacing:\r\n-.05pt\">the</span>o Đ<span style=\"letter-spacing:.05pt\">i</span><span style=\"letter-spacing:-.05pt\">ề</span>u 12 Ng<span style=\"letter-spacing:-.05pt\">h</span>ị\r\n<span style=\"letter-spacing:-.05pt\">đ</span><span style=\"letter-spacing:.1pt\">ị</span><span style=\"letter-spacing:-.05pt\">nh </span>135/2004/N<span style=\"letter-spacing:\r\n.05pt\">Đ</span>-CP;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;text-autospace:none\"><span style=\"letter-spacing:.05pt\">-</span> <span style=\"letter-spacing:-.05pt\">T</span>ài <span style=\"letter-spacing:.05pt\">l</span>iệu,<span style=\"letter-spacing:1.55pt\"> </span><span style=\"letter-spacing:.05pt\">h</span>ồ\r\n<span style=\"letter-spacing:.05pt\">s</span>ơ q<span style=\"letter-spacing:-.05pt\">u</span>ản\r\n<span style=\"letter-spacing:.05pt\">l</span>ý <span style=\"letter-spacing:-.05pt\">h</span>ọc\r\n<span style=\"letter-spacing:.05pt\">vi</span><span style=\"letter-spacing:-.05pt\">ê</span>n\r\n<span style=\"letter-spacing:.05pt\">t</span>r<span style=\"letter-spacing:.05pt\">on</span>g<span style=\"letter-spacing:1.55pt\"> </span><span style=\"letter-spacing:.05pt\">qu</span>á\r\n<span style=\"letter-spacing:.05pt\">tr</span><span style=\"letter-spacing:-.05pt\">ì</span>nh\r\ncai <span style=\"letter-spacing:.05pt\">n</span><span style=\"letter-spacing:\r\n-.05pt\">g</span><span style=\"letter-spacing:.05pt\">h</span>i<span style=\"letter-spacing:-.05pt\">ệ</span>n tại T<span style=\"letter-spacing:-.05pt\">r</span><span style=\"letter-spacing:.05pt\">un</span>g<span style=\"letter-spacing:1.55pt\"> </span><span style=\"letter-spacing:.05pt\">tâm </span>Ch<span style=\"letter-spacing:.05pt\">ữ</span>a<span style=\"letter-spacing:.05pt\"> </span>b<span style=\"letter-spacing:-.05pt\">ệ</span><span style=\"letter-spacing:.05pt\">n</span>h - <span style=\"letter-spacing:.05pt\">Giá</span>o<span style=\"letter-spacing:.1pt\"> </span><span style=\"letter-spacing:-.1pt\">d</span>ục\r\n-<span style=\"letter-spacing:.05pt\"> </span><span style=\"letter-spacing:-.05pt\">La</span>o<span style=\"letter-spacing:.1pt\"> </span><span style=\"letter-spacing:.05pt\">đ</span>ộ<span style=\"letter-spacing:-.05pt\">n</span>g<span style=\"letter-spacing:.05pt\"> x</span>ã\r\nhội.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify;mso-pagination:none\">2.&nbsp; Bản nhận xét, đánh giá của Giám đốc Trung tâm\r\nChữa bệnh – Giáo dục – Lao <span style=\"letter-spacing:-.4pt\">động xã hội về\r\nquá trình học tập, rèn luyện tại Trung tâm của người cai nghiện ma túy;</span><o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify;mso-pagination:none\">3. Văn bản đề\r\nnghị áp dụng biện pháp quản lý sau cai nghiện của Giám đốc Trung tâm Chữa bệnh\r\n– Giáo dục – Lao động xã hội;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify;mso-pagination:none\"><span style=\"letter-spacing:-.4pt\">4. Các tài liệu chứng minh đối tượng thuộc diện\r\nquản lý sau cai nghiện tại nơi cư trú.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>b) Số\r\nlượng hồ sơ:</i></b>&nbsp; (01 bộ)<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Thời hạn giải quyết: </b>10 ngày làm\r\nviệc kể từ khi nhận đủ hồ sơ hợp lệ.<b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cơ quan thực hiện:&nbsp; <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">a- Cơ quan có\r\nthẩm quyền quyết định theo quy định: Chủ tịch UBND cấp huyện<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">b- Cơ quan trực\r\ntiếp thực hiện: Phòng Lao động - TB&amp;XH <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Đối tượng thực hiện:&nbsp; </b>Tổ chức&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b> Kết quả: </b>Quyết định hành chính&nbsp; <b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Phí, lệ phí: </b>Không<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Mẫu đơn, tờ khai: </b>Không<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Yêu cầu, điều kiện:</b> <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Đối tượng bị áp\r\ndụng biện pháp quản lý sau cai nghiện tại nơi cư trú là người đã hoàn thành\r\nxong thời gian cai nghiện tại Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội,\r\nkhông thuộc đối tượng <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">a) Có thời gian nghiện ma túy từ năm năm\r\ntrở lên hoặc sử dụng ma túy với hình thức tiêm chích từ hai năm trở lên (xác\r\nđịnh theo hồ sơ áp dụng biện pháp đưa vào Trung tâm Chữa bệnh – Giáo dục – Lao\r\nđộng xã hội);<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">b) Đã cai nghiện bắt buộc tại Trung tâm\r\nChữa bệnh – Giáo dục – Lao động xã hội từ ba lần trở lên;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">c) Trong thời gian sáu tháng, đối tượng có\r\nhành vi vi phạm nội quy, quy chế của Trung tâm Chữa bệnh – Giáo dục – Lao động\r\nxã hội bị thi hành kỷ luật với hình thức cảnh cáo từ ba lần trở lên hoặc với\r\nhình thức cách ly tại phòng kỷ luật từ 2 lần trở lên;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">d) Không có nghề nghiệp; có nghề nghiệp nhưng\r\nkhông có việc làm ổn định trước khi vào cơ sở cai nghiện ma túy; không có nơi\r\ncư trú nhất định<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><i>(Nghị định số 94/2009/NĐ-CP ngày 26 tháng 10 năm 2009&nbsp;của\r\nChính phủ)<o:p></o:p></i></p><p class=\"MsoNormal\" style=\"margin-top:3.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Căn cứ pháp lý:&nbsp;&nbsp; <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">-Luật Phòng, chống ma túy ngày 09 tháng 12\r\nnăm 2000;<o:p></o:p></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"text-align:justify\">- Nghị định số 94/2009/NĐ-CP ngày\r\n26 tháng 10 năm 2009&nbsp;của Chính phủ Quy định chi tiết thi hành Luật sửa đổi\r\nbổ sung một số điều củaLuật Phòng, chống ma túy về quản lý sau cai nghiện ma\r\ntúy.<o:p></o:p></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (99,'LDH26A','Giảm thời hạn, tạm đình chỉ hoặc miễn chấp hành thời gian quản lý sau cai nghiện tại nơi cư trú',16,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Trình tự thực hiện: <o:p></o:p></b></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n1:</b> Người sau cai nghiện đã chấp hành được hai phần ba thời gian theo quyết\r\nđịnh quản lý sau cai nghiện, nếu lập công hoặc có tiến bộ rõ rệt thì Chủ tịch\r\nỦy ban nhân dân cấp xã đề nghị Chủ tịch Ủy ban nhân dân cấp huyện quyết định\r\ngiảm một phần, tạm đình chỉ hoặc miễn chấp hành thời gian còn lại.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n2:</b> Trong thời hạn ba ngày, kể từ ngày nhận văn bản của Chủ tịch Ủy ban nhân\r\ndân cấp xã, Trưởng Phòng Lao động – Thương binh và Xã hội cấp huyện phải thẩm\r\ntra, làm văn bản trình Chủ tịch Ủy ban nhân dân cấp huyện.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n3:</b> Trong thời hạn hai ngày, kể từ ngày nhận văn bản của Trưởng Phòng Lao\r\nđộng – Thương binh và Xã hội cấp huyện, Chủ tịch Ủy ban nhân dân cấp huyện phải\r\nxem xét, quyết định việc giảm thời hạn, tạm đình chỉ hoặc miễn việc chấp hành\r\nquyết định quản lý sau cai nghiện.<o:p></o:p></p><p class=\"bodytext1\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;\r\nmargin-left:0in;margin-bottom:.0001pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cách thức thực hiện:<o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Trực tiếp tại Bộ\r\nphận Tiếp nhận và trả kết quả- UBND cấp huyện<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Thành phần, số lượng hồ sơ:<o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>a)\r\nThành phần hồ sơ:<o:p></o:p></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;text-autospace:\r\nnone\">1. Danh sách đối tượng được đề nghị;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">2. Văn bản đề nghị của UBND cấp xã<span style=\"letter-spacing:-.4pt\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>b) Số\r\nlượng hồ sơ:</i></b>&nbsp; (01 bộ)<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Thời hạn giải quyết: </b>05 ngày làm\r\nviệc kể từ khi nhận đủ hồ sơ hợp lệ.<b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cơ quan thực hiện:&nbsp; <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">a- Cơ quan có\r\nthẩm quyền quyết định theo quy định: Chủ tịch UBND cấp huyện<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">b- Cơ quan trực\r\ntiếp thực hiện: Phòng Lao động - TB&amp;XH <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Đối tượng thực hiện:&nbsp; </b>Tổ chức&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Kết quả: </b>Quyết định hành chính&nbsp; <b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Phí, lệ phí: </b>Không<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Mẫu đơn, tờ khai: </b>Không<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Yêu cầu, điều kiện:</b> <o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">1.Người sau cai nghiện đã chấp hành\r\nđược hai phần ba thời gian theo quyết định quản lý sau cai nghiện, nếu lập công\r\nhoặc có tiến bộ rõ rệt thì được xem xét giảm một phần hoặc miễn chấp hành thời\r\ngian còn lại.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">2. Trường hợp người đang chấp hành\r\nquyết định quản lý sau cai nghiện bị ốm nặng, phụ nư thì được tạm đình chỉ chấp\r\nhành quyết định để điều trị; thời gian điều trị không được tính vào thời hạn\r\nchấp hành quyết định.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">Đối với phụ nữ có thai thì được tạm\r\nđình chỉ thi hành quyết định cho đến khi con đủ ba mươi sáu tháng tuổi. Nếu\r\ntrong thời gian tạm đình chỉ mà người đó có tiến bộ rõ rệt hoặc lập công được\r\nỦy ban nhân dân cấp xã nơi cư trú xác nhận thì được miễn chấp hành phần thời\r\ngian còn lại.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Đối với người\r\nmắc bệnh hiểm nghèo có chứng nhận của bệnh viện cấp huyện trở lên thì được <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><i>(Nghị định số 94/2009/NĐ-CP ngày 26 tháng 10\r\nnăm 2009&nbsp;của Chính phủ)<o:p></o:p></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Căn cứ pháp lý:&nbsp;&nbsp; <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">-Luật Phòng,\r\nchống ma túy ngày 09 tháng 12 năm 2000;<o:p></o:p></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"p0\" style=\"margin-top:6.0pt;text-align:justify\">- Nghị định số\r\n94/2009/NĐ-CP ngày 26 tháng 10 năm 2009&nbsp;của Chính phủ Quy định chi tiết\r\nthi hành Luật sửa đổi bổ sung một số điều củaLuật Phòng, chống ma túy về quản\r\nlý sau cai nghiện ma túy.<b><o:p></o:p></b></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (100,'LDH27A','Quyết định áp dụng biện pháp quản lý sau cai nghiện tại Trung tâm',16,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Trình tự thực hiện: <o:p></o:p></b></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n1:</b> Trước khi kết thúc thời gian cai nghiện bắt buộc bốn mươi lăm ngày, Giám\r\nđốc Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội có trách nhiệm lập hồ sơ,\r\ntổ chức Hội đồng xét duyệt người có nguy cơ tái nghiện cao và lập hồ sơ đề nghị\r\náp dụng biện pháp quản lý sau cai nghiện tại Trung tâm.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n2:</b> Trong thời hạn mười ngày, kể từ ngày nhận được hồ sơ quy định Trưởng\r\nPhòng Lao động – Thương binh và Xã hội có trách nhiệm thẩm tra hồ sơ và trình\r\nChủ tịch Ủy ban nhân dân cấp huyện.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n3:</b> Trong thời hạn năm ngày, kể từ ngày nhận được văn bản đề nghị của Trưởng\r\nPhòng Lao động – Thương binh và Xã hội, Chủ tịch Ủy ban nhân dân cấp huyện xem\r\nxét, quyết định áp dụng biện pháp quản lý sau cai nghiện tại Trung tâm. <o:p></o:p></p><p class=\"bodytext1\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;\r\nmargin-left:0in;margin-bottom:.0001pt;text-align:justify\">Quyết định được gửi\r\ncho người sau cai nghiện, gia đình người đó, Trung tâm Chữa bệnh – Giáo dục –\r\nLao động xã hội đang quản lý người sau cai nghiện, Trung tâm quản lý sau cai\r\nnghiện, Sở Lao động – Thương binh và Xã hội.<o:p></o:p></p><p class=\"bodytext1\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;\r\nmargin-left:0in;margin-bottom:.0001pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cách thức thực hiện:<o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Trực tiếp tại Bộ\r\nphận Tiếp nhận và trả kết quả- UBND cấp huyện<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Thành phần, số lượng hồ sơ:<o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>a)\r\nThành phần hồ sơ:<o:p></o:p></i></b></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">1. Bản sao hồ sơ của đối tượng cai\r\nnghiện bắt buộc tại Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội gồm:<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;text-autospace:\r\nnone\"><span style=\"letter-spacing:.05pt\">-</span><span style=\"letter-spacing:\r\n.35pt\"> </span>Hồ<span style=\"letter-spacing:.4pt\"> </span>sơ<span style=\"letter-spacing:.45pt\"> </span><span style=\"letter-spacing:-.05pt\">đ</span>ề<span style=\"letter-spacing:.45pt\"> </span>ng<span style=\"letter-spacing:-.05pt\">h</span>ị<span style=\"letter-spacing:.5pt\"> </span><span style=\"letter-spacing:-.05pt\">á</span>p<span style=\"letter-spacing:.4pt\"> </span><span style=\"letter-spacing:-.05pt\">d</span>ụ<span style=\"letter-spacing:.05pt\">n</span>g<span style=\"letter-spacing:.4pt\"> </span><span style=\"letter-spacing:.05pt\">b</span>i<span style=\"letter-spacing:-.05pt\">ệ</span>n<span style=\"letter-spacing:.4pt\"> </span>pháp<span style=\"letter-spacing:.4pt\"> </span><span style=\"letter-spacing:.05pt\">đ</span><span style=\"letter-spacing:-.05pt\">ư</span>a<span style=\"letter-spacing:.4pt\"> </span>v<span style=\"letter-spacing:-.05pt\">à</span>o<span style=\"letter-spacing:.5pt\"> </span>cơ<span style=\"letter-spacing:.35pt\"> </span><span style=\"letter-spacing:.05pt\">s</span>ở<span style=\"letter-spacing:.35pt\"> </span>ch<span style=\"letter-spacing:-.05pt\">ữ</span>a<span style=\"letter-spacing:.5pt\"> </span>b<span style=\"letter-spacing:-.05pt\">ệ</span><span style=\"letter-spacing:.05pt\">n</span>h<span style=\"letter-spacing:.35pt\"> </span><span style=\"letter-spacing:.05pt\">t</span><span style=\"letter-spacing:-.05pt\">h</span>eo<span style=\"letter-spacing:.4pt\"> </span><span style=\"letter-spacing:.05pt\">q</span><span style=\"letter-spacing:-.05pt\">u</span>y<span style=\"letter-spacing:.4pt\"> </span>định<span style=\"letter-spacing:.45pt\"> </span><span style=\"letter-spacing:-.05pt\">t</span>ại Điều<span style=\"letter-spacing:.05pt\">\r\n</span>9<span style=\"letter-spacing:.05pt\"> </span>Ng<span style=\"letter-spacing:\r\n.05pt\">h</span>ị<span style=\"letter-spacing:.1pt\"> </span><span style=\"letter-spacing:-.05pt\">đ</span>ịnh<span style=\"letter-spacing:.05pt\"> </span>135/2004/<span style=\"letter-spacing:-.1pt\">N</span><span style=\"letter-spacing:-.05pt\">Đ</span><span style=\"letter-spacing:.05pt\">-C</span>P <span style=\"letter-spacing:.05pt\">h</span>oặc\r\nĐ<span style=\"letter-spacing:.05pt\">i</span><span style=\"letter-spacing:-.05pt\">ề</span>u<span style=\"letter-spacing:.05pt\"> </span>16<span style=\"letter-spacing:.05pt\"> </span>Nghị\r\n<span style=\"letter-spacing:.05pt\">đ</span><span style=\"letter-spacing:-.05pt\">ị</span>nh\r\n43/2005/<span style=\"letter-spacing:-.05pt\">N</span>Đ-CP;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;text-autospace:\r\nnone\"><span style=\"letter-spacing:.05pt\">-</span> <span style=\"letter-spacing:\r\n.05pt\">Q</span><span style=\"letter-spacing:-.05pt\">u</span>yết <span style=\"letter-spacing:.05pt\">đ</span>ịnh việc đ<span style=\"letter-spacing:\r\n.05pt\">ư</span>a <span style=\"letter-spacing:.05pt\">v</span>ào cơ sở <span style=\"letter-spacing:-.05pt\">c</span>h<span style=\"letter-spacing:.05pt\">ữ</span>a\r\nbệ<span style=\"letter-spacing:-.05pt\">n</span>h <span style=\"letter-spacing:\r\n-.05pt\">the</span>o Đ<span style=\"letter-spacing:.05pt\">i</span><span style=\"letter-spacing:-.05pt\">ề</span>u 12 Ng<span style=\"letter-spacing:-.05pt\">h</span>ị\r\n<span style=\"letter-spacing:-.05pt\">đ</span><span style=\"letter-spacing:.1pt\">ị</span><span style=\"letter-spacing:-.05pt\">nh </span>135/2004/N<span style=\"letter-spacing:\r\n.05pt\">Đ</span>-CP;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;text-autospace:\r\nnone\"><span style=\"letter-spacing:.05pt\">-</span> <span style=\"letter-spacing:\r\n-.05pt\">T</span>ài <span style=\"letter-spacing:.05pt\">l</span>iệu,<span style=\"letter-spacing:1.55pt\"> </span><span style=\"letter-spacing:.05pt\">h</span>ồ\r\n<span style=\"letter-spacing:.05pt\">s</span>ơ q<span style=\"letter-spacing:-.05pt\">u</span>ản\r\n<span style=\"letter-spacing:.05pt\">l</span>ý <span style=\"letter-spacing:-.05pt\">h</span>ọc\r\n<span style=\"letter-spacing:.05pt\">vi</span><span style=\"letter-spacing:-.05pt\">ê</span>n\r\n<span style=\"letter-spacing:.05pt\">t</span>r<span style=\"letter-spacing:.05pt\">on</span>g<span style=\"letter-spacing:1.55pt\"> </span><span style=\"letter-spacing:.05pt\">qu</span>á\r\n<span style=\"letter-spacing:.05pt\">tr</span><span style=\"letter-spacing:-.05pt\">ì</span>nh\r\ncai <span style=\"letter-spacing:.05pt\">n</span><span style=\"letter-spacing:\r\n-.05pt\">g</span><span style=\"letter-spacing:.05pt\">h</span>i<span style=\"letter-spacing:-.05pt\">ệ</span>n tại T<span style=\"letter-spacing:-.05pt\">r</span><span style=\"letter-spacing:.05pt\">un</span>g<span style=\"letter-spacing:1.55pt\"> </span><span style=\"letter-spacing:.05pt\">tâm </span>Ch<span style=\"letter-spacing:.05pt\">ữ</span>a<span style=\"letter-spacing:.05pt\"> </span>b<span style=\"letter-spacing:-.05pt\">ệ</span><span style=\"letter-spacing:.05pt\">n</span>h - <span style=\"letter-spacing:.05pt\">Giá</span>o<span style=\"letter-spacing:.1pt\"> </span><span style=\"letter-spacing:-.1pt\">d</span>ục\r\n-<span style=\"letter-spacing:.05pt\"> </span><span style=\"letter-spacing:-.05pt\">La</span>o<span style=\"letter-spacing:.1pt\"> </span><span style=\"letter-spacing:.05pt\">đ</span>ộ<span style=\"letter-spacing:-.05pt\">n</span>g<span style=\"letter-spacing:.05pt\"> x</span>ã\r\nhội.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">2. Bản kiểm điểm quá trình học tập,\r\nrèn luyện trong thời gian ở Trung tâm của người cai nghiện, có nhận xét đánh\r\ngiá của Giám đốc Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">3. Biên bản của Hội đồng xét duyệt\r\nngười có nguy cơ tái nghiện cao tại Trung tâm; các tài liệu chứng minh đối\r\ntượng thuộc diện quản lý sau cai nghiện tại Trung tâm;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">4. Văn bản đề nghị của Giám đốc Trung\r\ntâm Chữa bệnh – Giáo dục – Lao động xã hội gửi Trưởng Phòng Lao động – Thương\r\nbinh và Xã hội nơi lập hồ sơ đưa đối tượng vào Trung tâm Chữa bệnh – Giáo dục –\r\nLao động xã hội.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>b) Số\r\nlượng hồ sơ:</i></b>&nbsp; (01 bộ)<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Thời hạn giải quyết: </b>15 ngày làm\r\nviệc kể từ khi nhận đủ hồ sơ hợp lệ.<b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cơ quan thực hiện:&nbsp; <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">a- Cơ quan có\r\nthẩm quyền quyết định theo quy định: Chủ tịch UBND cấp huyện<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">b- Cơ quan trực\r\ntiếp thực hiện: Phòng Lao động - TB&amp;XH <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Đối tượng thực hiện:&nbsp; </b>Tổ chức&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Kết quả: </b>Quyết định hành chính&nbsp; <b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Phí, lệ phí: </b>Không<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Mẫu đơn, tờ khai: </b>Không<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Yêu cầu, điều kiện:</b> <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">. Đối tượng bị\r\náp dụng biện pháp quản lý sau cai nghiện tại Trung tâm là người đã hoàn thành\r\nxong thời gian cai nghiện ma túy tại Trung tâm Chữa bệnh – Giáo dục – Lao động\r\nxã hội, có nguy cơ tái nghiện cao thuộc một trong các trường hợp sau đây: <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">a) Có thời gian\r\nnghiện ma túy từ năm năm trở lên hoặc sử dụng ma túy với hình thức tiêm chích\r\ntừ hai năm trở lên (xác định theo hồ sơ áp dụng biện pháp đưa vào Trung tâm\r\nChữa bệnh – Giáo dục – Lao động xã hội);<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">b) Đã cai nghiện\r\nbắt buộc tại Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội từ ba lần trở\r\nlên;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">c) Trong thời\r\ngian sáu tháng, đối tượng có hành vi vi phạm nội quy, quy chế của Trung tâm\r\nChữa bệnh – Giáo dục – Lao động xã hội bị thi hành kỷ luật với hình thức cảnh\r\ncáo từ ba lần trở lên hoặc với hình thức cách ly tại phòng kỷ luật từ 2 lần trở\r\nlên;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">d) Không có nghề\r\nnghiệp; có nghề nghiệp nhưng không có việc làm ổn định trước khi vào cơ sở cai\r\nnghiện ma túy; không có nơi cư trú nhất định<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">2. Không áp dụng\r\nbiện pháp quản lý sau cai nghiện tại Trung tâm đối với người đã hoàn thành thời\r\ngian cai nghiện ma túy bắt buộc, có nguy cơ tái nghiện cao theo quy định tại\r\nkhoản 1 Điều này nhưng không đủ sức khỏe để học nghề, lao động sản xuất hoặc bị\r\nbệnh AIDS giai đoạn cuối; người thuộc điểm a khoản 1 Điều này nhưng trong thời\r\ngian cai nghiện bắt buộc tại Trung tâm có ý thức chấp hành kỷ luật, học tập,\r\nrèn luyện tốt.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><i>(Nghị định số 94/2009/NĐ-CP ngày 26 tháng 10 năm 2009&nbsp;của\r\nChính phủ)<o:p></o:p></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Căn cứ pháp lý:&nbsp;&nbsp; <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">-Luật Phòng,\r\nchống ma túy ngày 09 tháng 12 năm 2000;<o:p></o:p></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">- Nghị định số\r\n94/2009/NĐ-CP ngày 26 tháng 10 năm 2009&nbsp;của Chính phủ Quy định chi tiết\r\nthi hành Luật sửa đổi bổ sung một số điều củaLuật Phòng, chống ma túy về quản\r\nlý sau cai nghiện ma túy.<b><o:p></o:p></b></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (101,'LDH29A','Hoãn hoặc miễn chấp hành quyết định áp dụng biện pháp quản lý sau cai nghiện tại Trung tâm',16,NULL,3,'<p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Trình tự thực hiện: <o:p></o:p></b></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n1:</b> Người sau cai nghiện làm đơn gửi Giám đốc Trung tâm Chữa bệnh – Giáo dục\r\n– Lao động xã hội hoặc Giám đốc Trung tâm quản lý sau cai nghiện;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">Đối với người đã có quyết định nhưng\r\nchưa chuyển sang Trung tâm quản lý sau cai nghiện thì Giám đốc Trung tâm Chữa\r\nbệnh – Giáo dục – Lao động xã hội tiếp nhận đơn đề nghị hoãn, miễn; đối với\r\nngười đang chấp hành quyết định tại Trung tâm quản lý sau cai nghiện thì Giám\r\nđốc Trung tâm quản lý sau cai nghiện tiếp nhận đơn đề nghị hoãn, miễn.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n2:</b> Trong thời hạn hai ngày, kể từ ngày nhận đơn, Giám đốc Trung tâm Chữa\r\nbệnh – Giáo dục – Lao động xã hội, Giám đốc Trung tâm quản lý sau cai nghiện\r\nlàm văn bản gửi Trưởng Phòng Lao động – Thương binh và Xã hội cấp huyện nơi lập\r\nhồ sơ đưa đối tượng vào Trung tâm Chữa bệnh – Giáo dục – Lao động xã hội;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n3:</b> Trong thời hạn ba ngày, kể từ ngày nhận văn bản của Giám đốc Trung tâm\r\nChữa bệnh – Giáo dục – Lao động xã hội, Giám đốc Trung tâm quản lý sau cai\r\nnghiện, Trưởng Phòng Lao động – Thương binh và Xã hội phải thẩm tra, làm văn\r\nbản trình Chủ tịch Ủy ban nhân dân cấp huyện;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\"><b>Bước\r\n4</b>: Trong thời hạn hai ngày, kể từ ngày nhận văn bản của Trưởng Phòng Lao\r\nđộng – Thương binh và Xã hội, Chủ tịch Ủy ban nhân dân cấp huyện phải xem xét,\r\nquyết định việc hoãn, miễn chấp hành quyết định áp dụng biện pháp quản lý sau\r\ncai nghiện tại Trung tâm.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cách thức thực hiện:<o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Trực tiếp tại Bộ\r\nphận Tiếp nhận và trả kết quả- UBND cấp huyện<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Thành phần, số lượng hồ sơ:<o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>a)\r\nThành phần hồ sơ:<o:p></o:p></i></b></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">1. Văn bản đề nghị của Giám đốc Trung\r\ntâm Chữa bệnh – Giáo dục – Lao động xã hội, Giám đốc Trung tâm quản lý sau cai\r\nnghiện <span style=\"letter-spacing:-.4pt\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i>b) Số\r\nlượng hồ sơ:</i></b>&nbsp; (01 bộ)<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Thời hạn giải quyết: </b>05 ngày làm\r\nviệc kể từ khi nhận đủ hồ sơ hợp lệ.<b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Cơ quan thực hiện:&nbsp; <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">a- Cơ quan có\r\nthẩm quyền quyết định theo quy định: Chủ tịch UBND cấp huyện<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">b- Cơ quan trực\r\ntiếp thực hiện: Phòng Lao động - TB&amp;XH <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Đối tượng thực hiện:&nbsp; </b>Tổ chức&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Kết quả: </b>Quyết định hành chính&nbsp; <b><o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Phí, lệ phí: </b>Không<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Mẫu đơn, tờ khai: </b>Không<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Yêu cầu, điều kiện:</b> <o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">1. Người sau cai nghiện được hoãn thi\r\nhành quyết định trong các trường hợp sau đây: <o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">a) Đang ốm nặng có chứng nhận của cơ\r\nsở y tế từ cấp huyện trở lên;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">b) Gia đình có hoàn cảnh đặc biệt khó\r\nkhăn được Chủ tịch Ủy ban nhân dân cấp xã nơi người đó cư trú xác nhận;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">c) Phụ nữ có thai hoặc đang nuôi con\r\nnhỏ dưới ba mươi sáu tháng tuổi.<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">2. Người sau cai nghiện được miễn\r\nchấp hành quyết định trong các trường hợp sau đây: <o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">a) Đang mắc bệnh hiểm nghèo có chứng\r\nnhận của cơ sở y tế từ cấp huyện trở lên;<o:p></o:p></p><p style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:0in;\r\nmargin-bottom:.0001pt;text-align:justify\">b) Bệnh nhân AIDS giai đoạn cuối;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><i>(Nghị định số 94/2009/NĐ-CP ngày 26 tháng 10\r\nnăm 2009&nbsp;của Chính phủ)<o:p></o:p></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b>Căn cứ pháp lý:&nbsp;&nbsp; <o:p></o:p></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">-Luật Phòng,\r\nchống ma túy ngày 09 tháng 12 năm 2000;<o:p></o:p></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">- Nghị định số\r\n94/2009/NĐ-CP ngày 26 tháng 10 năm 2009&nbsp;của Chính phủ Quy định chi tiết\r\nthi hành Luật sửa đổi bổ sung một số điều củaLuật Phòng, chống ma túy về quản\r\nlý sau cai nghiện ma túy.<o:p></o:p></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (102,'LDX28Z','Cấp giấy chứng nhận gia đình liệt sỹ và trợ cấp ưu đãi đối với thân nhân liệt sỹ.',16,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Trình tự thực hiện.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\">Bước 1: </span></b><span lang=\"NL\">UBND cấp xã\r\nchuyển hồ sơ theo quy định đến Phòng Lao động- Thương binh và Xã hội.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"letter-spacing:-.2pt;\r\nmso-ansi-language:NL\">Bước 2</span></b><span lang=\"NL\" style=\"letter-spacing:\r\n-.2pt;mso-ansi-language:NL\">: Phòng Lao động - Thương binh và Xã hội trong thời\r\ngian 05 ngày kể từ ngày nhận được giấy tờ có trách nhiệm tổng hợp, lập danh\r\nsách, gửi hồ sơ đến Sở Lao động – Thương binh và Xã hội.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Cách thức thực hiện:</span></b><span lang=\"NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Trực tiếp tại Bộ\r\nphận Tiếp nhận và trả kết quả- UBND huyện.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Thành phần, số lượng hồ sơ.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span lang=\"NL\">a) Thành phần hồ sơ bao gồm: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">1. Giấy báo tử.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">2. Bản sao Bằng “ Tổ quốc ghi công”.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">3. Bản khai tình hình thân nhân liệt sĩ (Mẫu LS4)\r\ncó xác nhận của UBND cấp xã.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span lang=\"NL\">b) Số lượng hồ sơ:</span></i></b><span lang=\"NL\">&nbsp; 01\r\n(bộ)<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"letter-spacing:-.2pt;\r\nmso-ansi-language:NL\">Thời hạn giải quyết:</span></b><span lang=\"NL\" style=\"letter-spacing:-.2pt;mso-ansi-language:NL\"> 05 ngày làm việc kể từ ngày\r\nnhận đủ hồ sơ hợp lệ<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Đối tượng thực hiện:</span></b><span lang=\"NL\"> Tổ chức.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Cơ quan thực hiện.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện: Phòng Lao động -\r\nThương binh và xã hội.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Cơ quan phối hợp: Ủy ban nhân dân cấp xã.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"> Kết quả:</span></b><span lang=\"NL\"> Danh\r\nsách tổng hợp.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"> Phí, lệ phí:</span></b><span lang=\"NL\">\r\nKhông.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Tên mẫu đơn, mẫu tờ khai:</span></b><span lang=\"NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">1. Bản khai tình hình thân nhân liệt sĩ (Mẫu LS4)<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\"><i><span lang=\"NL\" style=\"letter-spacing:-.3pt;\r\nmso-ansi-language:NL\">&nbsp;( theo Thông tư số\r\n05/2013/TT-BLĐTBXH ngày 15 tháng 5 năm 2013)</span></i><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"> Yêu cầu, điều kiện:</span></b><span lang=\"NL\"> Không.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Căn cứ pháp lý.<o:p></o:p></span></b></p><h1 style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:\r\n-1.5pt;margin-bottom:.0001pt;text-align:justify;tab-stops:7.65pt list 28.5pt\"><b><span lang=\"NL\" style=\"font-size:12.0pt;\r\nmso-ansi-language:NL\">- </span></b><span lang=\"NL\" style=\"font-size:12.0pt;\r\nmso-ansi-language:NL\">Pháp lệnh số 04/2012/UBTVQH13 ngày 16 tháng 7 năm 2012\r\ncủa Ủy ban thường vụ Quốc hội sửa đổi, bổ sung một số điều của Pháp lệnh ưu đãi\r\nngười có công với cách mạng.<b><o:p></o:p></b></span></h1><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Nghị định số 31/2013/NĐ-CP ngày 09/4/2013 của\r\nChính phủ quy định chi tiết, hướng dẫn thi hành một số điều của Pháp lệnh Ưu\r\nđãi người có công với cách mạng.<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Thông tư số 05/2013/TT-BLĐTBXH ngày 15/5/2013\r\ncủa Bộ Lao động – Thương binh và Xã hội hướng dẫn về thủ tục lập hồ sơ, quản lý\r\nhồ sơ, thực hiện chế độ ưu đãi người có công với cách mạng và thân nhân.<o:p></o:p></span></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (103,'LDX29Z','Trợ cấp thờ cúng liệt sỹ.',16,NULL,2,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Trình tự thực hiện.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\">Bước 1: </span></b><span lang=\"NL\">UBND cấp xã\r\nchuyển hồ sơ theo quy định đến Phòng Lao động- Thương binh và Xã hội huyện,\r\nthành phố.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"letter-spacing:-.2pt;\r\nmso-ansi-language:NL\">Bước 2</span></b><span lang=\"NL\" style=\"letter-spacing:\r\n-.2pt;mso-ansi-language:NL\">: Phòng Lao động - Thương binh và Xã hội trong thời\r\ngian 10 ngày kể từ ngày tiếp nhận đủ hồ sơ hợp lệ, có trách nhiệm tổng hợp, lập\r\ndanh sách, gửi hồ sơ đến Sở Lao động – Thương binh và Xã hội.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Cách thức thực hiện:</span></b><span lang=\"NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">Trực tiếp tại Bộ\r\nphận Tiếp nhận và trả kết quả- UBND huyện.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Thành phần, số lượng hồ sơ.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span lang=\"NL\">a) Thành phần hồ sơ bao gồm: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\">1. Đơn đề nghị hưởng trợ cấp thờ cúng liệt sỹ ( Mẫu\r\nLS 7 ) có xác nhận của Ủy ban nhân dân cấp xã nơi cư trú.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\">2. Biên bản ủy quyền&nbsp;\r\n( Mẫu UQ ) có xác nhận của Ủy ban nhân dân cấp xã nơi cư trú.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\">3. Danh sách tổng hợp.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span lang=\"NL\">b) Số lượng hồ sơ:</span></i></b><span lang=\"NL\">&nbsp; 01\r\n(bộ)<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"letter-spacing:-.2pt;\r\nmso-ansi-language:NL\">Thời hạn giải quyết:</span></b><span lang=\"NL\" style=\"letter-spacing:-.2pt;mso-ansi-language:NL\"> 10 ngày kể từ ngày nhận đủ\r\nhồ sơ.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Đối tượng thực hiện:</span></b><span lang=\"NL\"> Tổ chức.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Cơ quan thực hiện.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện: Phòng Lao động - Thương\r\nbinh và xã hội.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Cơ quan phối hợp: Ủy ban nhân dân cấp xã.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Kết quả:</span></b><span lang=\"NL\"> Danh\r\nsách tổng hợp.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Phí, lệ phí:</span></b><span lang=\"NL\">\r\nKhông.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Tên mẫu đơn, mẫu tờ khai:</span></b><span lang=\"NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">1. Đơn đề nghị\r\nhưởng trợ cấp thờ cúng liệt sỹ (mẫu LS7) <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">2. Biên bản ủy\r\nquyền (Mẫu UQ)<span lang=\"NL\">.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\"><i><span lang=\"NL\" style=\"letter-spacing:-.3pt;\r\nmso-ansi-language:NL\">&nbsp;(&nbsp; theo Thông tư số 05/2013/TT-BLĐTBXH ngày 15\r\ntháng 5 năm 2013)</span></i><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Yêu cầu, điều kiện:</span></b><span lang=\"NL\"> Không.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Căn cứ pháp lý.<o:p></o:p></span></b></p><h1 style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:0in;margin-left:\r\n-1.5pt;margin-bottom:.0001pt;text-align:justify;tab-stops:7.65pt list 28.5pt\"><b><span lang=\"NL\" style=\"font-size:12.0pt;\r\nmso-ansi-language:NL\">- </span></b><span lang=\"NL\" style=\"font-size:12.0pt;\r\nmso-ansi-language:NL\">Pháp lệnh số 04/2012/UBTVQH13 ngày 16 tháng 7 năm 2012\r\ncủa Ủy ban thường vụ Quốc hội sửa đổi, bổ sung một số điều của Pháp lệnh ưu đãi\r\nngười có công với cách mạng.<b><o:p></o:p></b></span></h1><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Nghị định số 31/2013/NĐ-CP ngày 09/4/2013 của\r\nChính phủ quy định chi tiết, hướng dẫn thi hành một số điều của Pháp lệnh Ưu\r\nđãi người có công với cách mạng.<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Thông tư số 05/2013/TT-BLĐTBXH ngày 15/5/2013\r\ncủa Bộ Lao động – Thương binh và Xã hội hướng dẫn về thủ tục lập hồ sơ, quản lý\r\nhồ sơ, thực hiện chế độ ưu đãi người có công với cách mạng và thân nhân.<o:p></o:p></span></p>',NULL,'',1);
