insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (105,'TPH01A','Cấp bản sao giấy tờ hộ tịch từ sổ hộ tịch đang lưu trữ tại UBND cấp huyện',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Trình tự thực hiện</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n1:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Người\r\nyêu cầu cấp bản bản sao giấy tờ hộ tịch có thể trực tiếp đề nghị hoặc </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">có\r\nthể gửi đề nghị qua đường bưu điện đến Bộ phận tiếp nhận và trả kết quả - UBND\r\ncấp huyện. Cán bộ kiểm tra và tiếp nhận hồ sơ, công dân nộp lệ phí cấp bản sao\r\ngiấy tờ hộ tịch.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;tab-stops:right 6.3in\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">- Thời gian\r\nnhận hồ sơ: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.&nbsp;&nbsp; <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n2:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.2pt\">Cán bộ bộ phận </span><span lang=\"NL\" style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">tiếp nhận và trả kết quả </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;letter-spacing:-.2pt\">- UBND cấp\r\nhuyện kiểm tra hồ sơ, chuyển phòng Tư pháp</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">.</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n3:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;letter-spacing:-.2pt\">Phòng Tư\r\npháp tra cứu hồ sơ lưu, trình UBND cấp huyện ký bản sao giấy tờ hộ tịch</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n4:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.2pt\">Người yêu cầu </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">cấp\r\nbản bản sao giấy tờ hộ tịch<span style=\"letter-spacing:-.2pt\"> nhận kết quả tại\r\nbộ phận </span></span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">tiếp nhận và trả kết quả</span><span style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;letter-spacing:-.2pt\"> - UBND cấp huyện</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">\r\nhoặc UBND cấp huyện gửi cho người yêu cầu qua đường bưu điện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2\r\nđến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> <b>Cách thức thực hiện<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Trực tiếp hoặc gửi qua đường bưu điện đến Bộ phận tiếp\r\nnhận và trả kết quả của UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Thành phần, số lượng hồ sơ<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:5.65pt;margin-bottom:\r\n6.0pt;margin-left:-3.1pt;text-align:justify;mso-pagination:none;mso-layout-grid-align:\r\nnone;text-autospace:none\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">&nbsp;a) Thành phần hồ\r\nsơ: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">1- Xuất trình chứng minh thư nhân dân hoặc hộ chiếu hoặc\r\ngiấy tờ tùy thân khác;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">2- Xuất trình thêm giấy tờ chứng minh là người có quyền\r\nyêu cầu cấp bản sao từ sổ gốc nếu là:<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Người đại diện hợp pháp, người được uỷ quyền của người\r\nđược cấp bản chính;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Cha, mẹ, con, vợ, chồng, anh, chị, em ruột, người thừa\r\nkế của người được cấp bản chính trong trường hợp người đó đã chết.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">3- Trong trường hợp yêu cầu cấp bản sao từ sổ gốc qua bưu\r\nđiện thì người yêu cầu phải gửi đầy đủ các giấy tờ được quy định tại khoản này\r\n(bản chính hoặc bản sao có chứng thực).<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">b) Số bộ hồ sơ:</span></i></b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nNL\"> </span></i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">01 (bộ)<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Thời hạn giải quyết: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Trong ngày.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Trong trường hợp yêu cầu được gửi qua bưu điện thì chậm\r\nnhất là trong 3 ngày làm việc, kể từ ngày nhận được yêu cầu (theo dấu ngày đến\r\ncủa bưu điện), cơ quan, tổ chức cấp bản sao phải gửi bản sao cho người yêu cầu</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">.</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Đối tượng thực hiện: </b>Cá nhân<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;tab-stops:210.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nNL;mso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Cơ quan thực hiện&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Cơ quan có thẩm quyền quyết định theo quy định: UBND\r\ncấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp\r\nhuyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Cơ quan phối hợp: </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Văn\r\nphòng HĐND và UBND cấp huyện.</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Kết quả: </b>Bản sao giấy tờ hộ tịch<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Phí, lệ phí:\r\n<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-\r\nLệ phí cấp bản sao giấy tờ hộ tịch từ sổ hộ tịch: 3.000 đ/bản</span><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-font-kerning:18.0pt;mso-ansi-language:NL;mso-bidi-font-weight:bold\"> <o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Người yêu cầu cấp bản sao qua bưu điện phải trả lệ phí\r\ncấp bản sao và cước phí bưu điện cho cơ quan cấp bản sao.<i><o:p></o:p></i></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-font-kerning:18.0pt;\r\nmso-ansi-language:NL;mso-bidi-font-weight:bold\">(</span></i><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Nghị quyết số 14/2009/NQ-HĐND ngày 09/12/2009 của Hội\r\nđồng nhân dân tỉnh Bắc Giang )<o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Tên mẫu đơn, mẫu tờ khai: </b>Không<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Yêu cầu, điều kiện: </span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Không<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Căn cứ pháp lý<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị định số 158/2005/NĐ-CP ngày 27/12/2005 của Chính\r\nphủ về đăng ký quản lý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị định số 06/2012/NĐ-CP ngày 02/02/2012 của Chính\r\nphủ về sửa đổi, bổ sung một số điều của các Nghị định về hộ tịch, hôn nhân gia\r\nđình và chứng thực;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị định số 79/2007/NĐ-CP ngày 18/5/2007 của Chính phủ\r\nvề cấp bản sao từ sổ gốc, chứng thực bản sao từ bản chính, chứng thực chữ ký;<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị quyết số 14/2009/NQ-HĐND ngày 09/12/2009 của Hội\r\nđồng nhân dân tỉnh sửa đổi, bổ sung, thay thế một số loại phí, lệ phí ban hành\r\nkèm theo nghị quyết số 11/2007/NQ-HĐND ngày 19/72007 của Hội đồng nhân dân tỉnh\r\nkhóa XVI.<o:p></o:p></span></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (106,'TPH02A','Thủ tục Cấp lại bản chính giấy khai sinh ',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;line-height:15.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span>Trình tự thực hiện</b><u1:p></u1:p><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Bước 1:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">&nbsp;</span></span><span style=\"font-size: 10.5pt;\">Người\r\nyêu cầu cấp bản chính Giấy khai sinh có thể nộp trực tiếp hoặc gửi hồ sơ qua hệ\r\nthống bưu chính</span><span class=\"apple-converted-space\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">&nbsp;</span></span><span lang=\"NL\" style=\"font-size: 10.5pt;\">đến Bộ phận tiếp nhận và trả kết quả - UBND\r\ncấp huyện. Cán bộ kiểm tra và tiếp nhận hồ sơ, công dân nộp lệ phí cấp lại bản\r\nchính Giấy khai sinh.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Thời gian nhận hồ sơ: Trong giờ hành chính\r\ntừ thứ 2 đến thứ 6 hàng tuần.&nbsp;&nbsp;<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Bước 2:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">&nbsp;</span></span><span style=\"font-size: 10.5pt;\">Sau\r\nkhi nhận đủ giấy tờ hợp lệ, cán bộ Tư pháp của Phòng Tư pháp căn cứ vào Sổ đăng\r\nký khai sinh đang lưu trữ để ghi vào nội dung bản chính Giấy khai sinh,<span class=\"apple-converted-space\">&nbsp;</span></span><span lang=\"NL\" style=\"font-size: 10.5pt;\">nếu Sổ đăng\r\nký khai sinh chỉ lưu tại Uỷ ban nhân dân cấp xã, thì Uỷ ban nhân dân cấp huyện\r\nyêu cầu Uỷ ban nhân dân cấp xã cung cấp thông tin để ghi vào nội dung bản chính\r\nGiấy khai sinh. Uỷ ban nhân dân cấp xã có trách nhiệm trích lục thông tin trả\r\nlời bằng văn bản, hoặc sao chụp trang Sổ đăng ký khai sinh có xác nhận của Uỷ\r\nban nhân dân xã và gửi cho Uỷ ban nhân dân cấp huyện.</span><u1:p></u1:p><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Bước 3:</span></b><span class=\"apple-converted-space\">&nbsp;</span><span style=\"font-size: 10.5pt;\">Chủ tịch Ủy ban nhân dân cấp huyện ký và\r\ncấp cho đương sự một bản chính Giấy khai sinh mới, thu hồi lại Giấy khai sinh\r\ncũ (nếu có).<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Bước 4:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">&nbsp;</span></span><span lang=\"NL\" style=\"font-size: 10.5pt;\">Khi cấp lại\r\nbản chính Giấy khai sinh, Uỷ ban nhân dân cấp huyện không phải lập sổ riêng mà\r\nchỉ ghi chú việc cấp lại bản chính trong cột ghi chú của Sổ đăng ký khai sinh\r\ntheo quy định tại Khoản 2 Điều 63 Nghị định số 158/2005/NĐ-CP. Số, quyển số ghi\r\ntrong bản chính Giấy khai sinh khi cấp lại được ghi theo số, quyển số của Sổ\r\nđăng ký khai sinh trước đây.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Bước 5:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">&nbsp;</span></span><span lang=\"NL\" style=\"font-size: 10.5pt;\">Công dân nhận\r\nkết quả tại Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Thời gian trả kết quả: Trong giờ hành chính\r\ntừ thứ 2 đến thứ 6 hàng tuần.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Cách thức\r\nthực hiện</span></b><b><u1:p></u1:p></b><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">Trực tiếp hoặc gửi qua đường bưu điện đến Bộ\r\nphận tiếp nhận và trả kết quả - UBND cấp huyện.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Thành phần,\r\nsố lượng hồ sơ</span></b><b><u1:p></u1:p></b><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 5.65pt 6pt -3.1pt; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\" style=\"font-size: 10.5pt;\">&nbsp;a) Thành phần hồ sơ:<u1:p></u1:p></span></i></b><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">1- Tờ khai (theo mẫu quy định);<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">2-<span class=\"apple-converted-space\">&nbsp;</span></span><span style=\"font-size: 10.5pt;\">Bản\r\nchính Giấy khai sinh cũ (nếu có);<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">3- Xuất trình<span class=\"apple-converted-space\">&nbsp;</span></span><span style=\"font-size: 10.5pt;\">Giấy chứng minh nhân dân hoặc Hộ chiếu của\r\nngười đi đăng ký hộ tịch để xác định về cá nhân người đó;</span><u1:p></u1:p><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">4- Văn bản uỷ quyền được công chứng hoặc\r\nchứng thực trong trường hợp<span class=\"apple-converted-space\">&nbsp;</span></span><span style=\"font-size: 10.5pt;\">người\r\ncó yêu cầu cấp lại bản chính giấy khai sinh mà không có điều kiện trực tiếp\r\nthực hiện thủ tục. Nếu người được ủy quyền là ông, bà, cha, mẹ, con, vợ, chồng,\r\nanh, chị, em ruột của người ủy quyền, thì không cần phải có văn bản ủy quyền,\r\nnhưng phải có giấy tờ chứng minh về mối quan hệ nêu trên</span><span lang=\"NL\" style=\"font-size: 10.5pt;\">.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\" style=\"font-size: 10.5pt;\">b) Số bộ hồ sơ:</span></i></b><span class=\"apple-converted-space\"><i><span lang=\"NL\" style=\"font-size: 10.5pt;\">&nbsp;</span></i></span><span lang=\"NL\" style=\"font-size: 10.5pt;\">01 (bộ).<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span>Thời hạn giải quyết:<u1:p></u1:p></b><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Trong ngày.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">-<span class=\"apple-converted-space\">&nbsp;</span></span><span style=\"font-size: 10.5pt;\">Trường\r\nhợp cần phải xác minh, thì thời hạn nói trên được kéo dài thêm không quá 03\r\nngày làm việc.</span><u1:p></u1:p><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Đối tượng\r\nthực hiện:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Cá nhân<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Cơ quan thực\r\nhiện&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></b><b><u1:p></u1:p></b><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Cơ quan có thẩm quyền quyết định theo quy\r\nđịnh: UBND cấp huyện.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Cơ quan trực tiếp thực hiện TTHC: UBND cấp\r\nhuyện.<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Kết quả:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Giấy khai sinh<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Phí, lệ phí:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Lệ phí cấp lại bản chính Giấy khai sinh:\r\n10.000đ</span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><i><span lang=\"NL\" style=\"font-size: 10.5pt;\">(</span>Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007 của\r\nHội đồng nhân dân tỉnh Quy định mức thu và tỷ lệ điều tiết các loại phí, lệ phí\r\ncủa tỉnh Bắc Giang)<u1:p></u1:p></i><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Tên mẫu đơn,\r\nmẫu tờ khai:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Tờ khai cấp\r\nlại bản chính giấy khai sinh<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><i><span lang=\"NL\" style=\"font-size: 10.5pt;\">(Thông tư số\r\n05/2012/TT-BTP ngày 23/5/2012 của Bộ Tư pháp)</span></i><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span>Yêu cầu, điều kiện:<span class=\"apple-converted-space\">&nbsp;</span></b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Không<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-size: 10.5pt;\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\" style=\"font-size: 10.5pt;\">Căn cứ pháp lý<u1:p></u1:p></span></b><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Nghị định số 158/2005/NĐ-CP ngày 27/12/2005\r\ncủa Chính phủ về đăng ký quản lý hộ tịch;<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Nghị định số 06/2012/NĐ-CP ngày 02/02/2012\r\ncủa Chính phủ về sửa đổi, bổ sung một số điều của các Nghị định về hộ tịch, hôn\r\nnhân gia đình và chứng thực;<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Thông tư số 01/2008/TT-BTP ngày 02/6/2008\r\ncủa Bộ Tư pháp Hướng dẫn thực hiện một số quy định của Nghị định số\r\n158/2005/NĐ-CP ngày 27/12/2005 của Chính phủ về đăng ký và quản lý hộ tịch;<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Thông tư số 05/2012/TT-BTP ngày 23/5/2012\r\ncủa Bộ Tư pháp về Sửa đổi, bổ sung một số điều của Thông tư số 08.a/2010/TT-BTP\r\nngày 25 tháng 3 năm 2010 của Bộ Tư pháp về việc ban hành và hướng dẫn việc ghi\r\nchép, lưu trữ, sử dụng sổ, biểu mẫu hộ tịch;<u1:p></u1:p></span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<u1:p></u1:p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<u1:p></u1:p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p style=\"margin: 0in 0in 7.5pt; line-height: 15pt;\"><span lang=\"NL\" style=\"font-size: 10.5pt;\">- Nghị quyết số 11/2007/NQ-HĐND ngày\r\n19/7/2007 của Hội đồng nhân dân tỉnh Quy định mức thu và tỷ lệ điều tiết các\r\nloại phí, lệ phí của tỉnh Bắc Giang.</span><span style=\"font-size: 10.5pt; font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (107,'TPH03A','Bổ sung hộ tịch, điều chỉnh hộ tịch cho mọi trường hợp không phân biệt độ tuổi',24,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Trình tự thực hiện</span></b><span lang=\"NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\">Bước 1:</span></b><span lang=\"NL\"> </span>Người yêu cầu bổ sung hộ tịch, điều chỉnh\r\nhộ tịch có thể nộp trực tiếp hoặc gửi hồ sơ qua hệ thống bưu chính<span lang=\"NL\"> đến Bộ phận tiếp nhận và trả kết quả -\r\nUBND cấp huyện. Cán bộ kiểm tra và tiếp nhận hồ sơ, công dân nộp lệ phí theo\r\nquy định.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thời gian nhận hồ sơ: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><b><span lang=\"NL\">Bước 2:</span></b><span lang=\"NL\"> Cán bộ Tư\r\npháp đóng dấu vào phần ghi bổ sung, điều chỉnh. Cột ghi chú của Sổ đăng ký khai\r\nsinh và mặt sau của bản chính Giấy khai sinh hoặc Sổ hộ tịch và giấy tờ hộ tịch\r\nkhác phải ghi rõ nội dung bổ sung, điều chỉnh; căn cứ ghi bổ sung, điều chỉnh;\r\nhọ, tên, chữ ký của người ghi bổ sung, điều chỉnh; ngày, tháng, năm bổ sung,\r\nđiều chỉnh. <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><span lang=\"NL\">Trong trường hợp nội dung Sổ\r\nđăng ký khai sinh và bản chính Giấy khai sinh trước đây không có cột mục cần\r\nghi bổ sung, thì nội dung bổ sung được ghi vào mặt sau của bản chính Giấy khai\r\nsinh và cột ghi chú của Sổ đăng ký khai sinh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\">Bước 3:</span></b><span lang=\"NL\"> Công dân nhận kết quả tại Bộ phận tiếp nhận và\r\ntrả kết quả - UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Cách thức thực hiện<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">Trực tiếp hoặc qua đường bưu điện đến Bộ phận tiếp nhận và trả kết quả -\r\nUBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Thành phần, số lượng hồ sơ<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:5.65pt;margin-bottom:\r\n6.0pt;margin-left:-3.1pt;text-align:justify;mso-pagination:none;mso-layout-grid-align:\r\nnone;text-autospace:none\"><b><i><span lang=\"NL\">&nbsp;a) Thành phần hồ sơ: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">1- Tờ khai (theo mẫu quy định);<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">2- </span>Xuất trình bản chính Giấy khai sinh của người cần bổ sung, điều\r\nchỉnh hộ tịch; <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">3- Trường hợp nội dung điều chỉnh không\r\nliên quan đến Giấy khai sinh, thì phải xuất trình các giấy tờ khác làm căn cứ\r\ncho việc điều chỉnh; <o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">4- Các giấy tờ liên quan để làm căn cứ cho\r\nviệc bổ sung hộ tịch<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">- Trường hợp trực tiếp nộp hồ sơ thì nộp\r\nbản sao kèm bản chính để đối chiếu hoặc bản sao có chứng thực;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">- Trường hợp hồ sơ gửi qua hệ thống bưu\r\nchính, thì các giấy tờ có trong thành phần hồ sơ phải là bản sao có chứng thực;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">5- Xuất trình </span>Giấy chứng minh nhân dân hoặc Hộ chiếu của người đi\r\nđăng ký hộ tịch để xác định về cá nhân người đó;<span lang=\"NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">6- Văn bản uỷ quyền được công chứng hoặc chứng thực trong trường hợp </span>người\r\ncó yêu cầu bổ sung, điều chỉnh mà không có điều kiện trực tiếp đến <span lang=\"NL\">thực hiện thủ tục</span>. Nếu người được\r\nủy quyền là ông, bà, cha, mẹ, con, vợ, chồng, anh, chị, em ruột của người ủy\r\nquyền, thì không cần phải có văn bản ủy quyền, nhưng phải có giấy tờ chứng minh\r\nvề mối quan hệ nêu trên<span lang=\"NL\">.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><i><span lang=\"NL\">b)\r\nSố bộ hồ sơ:</span></i></b><span lang=\"NL\"> 01 bộ.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Thời hạn giải quyết: </span></b><span lang=\"NL\">Trong ngày; </span>nếu nhận hồ sơ sau 15 giờ thì trả kết quả trong ngày làm\r\nviệc tiếp theo.<b><span lang=\"NL\"><o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Đối tượng thực hiện: </b>Cá nhân<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;tab-stops:210.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Cơ quan thực hiện&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in\"><span lang=\"NL\">- Cơ quan có thẩm\r\nquyền quyết định: Phòng Tư pháp cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Kết quả: </b>Giấy khai sinh đã được bổ sung\r\nhoặc Giấy tờ hộ tịch khác đã được điều chỉnh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Phí, lệ phí: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\">- </span></b><span lang=\"NL\">Lệ phí bổ sung hộ tịch: miễn thu; <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Lệ phí điều chỉnh hộ tịch: 25.000đ.<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\">(</span></i><i><span lang=\"NL\">Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007)<o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Tên mẫu đơn, mẫu tờ khai: </b><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Tờ khai điều chỉnh nội dung trong các giấy tờ hộ tịch khác;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Tờ khai đăng ký lại việc thay đổi, cải chính, bổ sung hộ tịch, xác định\r\nlại dân tộc, xác định lại giới tính trong giấy khai sinh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\">(Thông tư số 05/2012/TT-BTP ngày 23/5/2012\r\ncủa Bộ Tư pháp)<b><o:p></o:p></b></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Yêu cầu, điều kiện: </span></b><span lang=\"NL\">Không<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Căn cứ pháp lý<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Nghị định số 158/2005/NĐ-CP ngày 27/12/2005 của Chính phủ về đăng ký quản\r\nlý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Nghị định số 06/2012/NĐ-CP ngày 02/02/2012 của Chính phủ về sửa đổi, bổ\r\nsung một số điều của các Nghị định về hộ tịch, hôn nhân gia đình và chứng thực;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thông tư số 01/2008/TT-BTP ngày 02/6/2008 của Bộ Tư pháp Hướng dẫn thực\r\nhiện một số quy định của Nghị định số 158/2005/NĐ-CP ngày 27/12/2005 của Chính\r\nphủ về đăng ký và quản lý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thông tư số 05/2012/TT-BTP ngày 23/5/2012 của Bộ Tư pháp về Sửa đổi, bổ\r\nsung một số điều của Thông tư số 08.a/2010/TT-BTP ngày 25 tháng 3 năm 2010 của\r\nBộ Tư pháp về việc ban hành và hướng dẫn việc ghi chép, lưu trữ, sử dụng sổ,\r\nbiểu mẫu hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007 của Hội đồng nhân dân tỉnh\r\nBắc Giang Quy định mức thu và tỷ lệ điều tiết các loại phí, lệ phí của tỉnh Bắc\r\nGiang;<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\">- Nghị quyết số 20/2007/NQ-HĐND ngày 07/12/2007\r\ncủa Hội đồng nhân dân tỉnh Bắc Giang sửa đổi, bãi bỏ một số danh mục thu phí,\r\nlệ phí ban hành tại&nbsp; Nghị quyết số\r\n11/2007/NQ-HĐND ngày 19/7/2007 của Hội đồng nhân dân tỉnh Bắc Giang.</span><b><o:p></o:p></b></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (108,'TPH04A','Thủ tục Thay đổi, cải chính hộ tịch đối với người từ đủ 14 tuổi trở lên',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Trình tự thực hiện</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n1:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Người\r\nyêu cầu thay đổi, cải chính hộ tịch có thể nộp trực tiếp hoặc gửi hồ sơ qua hệ\r\nthống bưu chính</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> đến Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện.\r\nCán bộ kiểm tra và tiếp nhận hồ sơ, công dân nộp lệ phí.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thời gian nhận hồ sơ: Trong giờ hành chính từ thứ 2 đến\r\nthứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><b><span lang=\"NL\" style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước 2:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nếu việc thay đổi, cải chính hộ\r\ntịch có đủ điều kiện theo quy định của pháp luật, cán bộ Tư pháp của Phòng Tư\r\npháp ghi vào Sổ đã đăng ký khai sinh trước đây và Quyết định về việc thay đổi,\r\ncải chính hộ tịch. Chủ tịch Ủy ban nhân dân cấp huyện ký và cấp cho đương sự\r\nmột bản chính Quyết định về việc thay đổi, cải chính hộ tịch. Bản sao Quyết\r\nđịnh được cấp theo yêu cầu của đương sự.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nội dung và căn cứ\r\nthay đổi, cải chính hộ tịch phải được ghi chú vào cột ghi những thay đổi sau\r\nnày của Sổ đăng ký khai sinh và mặt sau của bản chính Giấy khai sinh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n3: </span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Sau khi thực hiện việc thay đổi, cải chính hộ tịch thì Ủy\r\nban nhân dân cấp huyện có trách nhiệm gửi thông báo cho Ủy ban nhân dân cấp xã,\r\nnơi đã đăng ký hộ tịch về những nội dung thay đổi để ghi tiếp vào sổ hộ tịch\r\nlưu tại Ủy ban nhân dân cấp xã.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n4:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> Công dân nhận kết quả tại Bộ phận tiếp nhận và trả kết\r\nquả - UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2\r\nđến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Cách thức thực hiện<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Trực tiếp hoặc gửi qua đường bưu điện đến Bộ phận tiếp\r\nnhận và trả kết quả - UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Thành phần, số lượng hồ sơ<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:5.65pt;margin-bottom:\r\n6.0pt;margin-left:-3.1pt;text-align:justify;mso-pagination:none;mso-layout-grid-align:\r\nnone;text-autospace:none\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">&nbsp;a) Thành phần hồ\r\nsơ: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">1- Tờ khai (theo mẫu quy định).<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">2- </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Xuất\r\ntrình bản chính Giấy khai sinh của người cần thay đổi, cải chính hộ tịch và các\r\ngiấy tờ liên quan để làm căn cứ cho việc thay đổi, cải chính hộ tịch.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-\r\nTrường hợp hồ sơ nộp trực tiếp thì nộp bản sao kèm bản chính để đối chiếu hoặc\r\nbản sao có chứng thực;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-\r\nTrường hợp gửi qua hệ thống bưu chính, thì các giấy tờ có trong thành phần hồ\r\nsơ phải là bản sao có chứng thực.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">3- Xuất trình </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Giấy\r\nchứng minh nhân dân hoặc Hộ chiếu của người đi đăng ký hộ tịch để xác định về\r\ncá nhân người đó.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4-\r\nXuất trình Sổ hộ khẩu, Sổ đăng ký tạm trú để làm căn cứ xác định thẩm quyền\r\nđăng ký hộ tịch.</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.3pt;mso-ansi-language:NL\">5- </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Văn bản uỷ\r\nquyền được công chứng hoặc chứng thực trong trường hợp </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">người có yêu cầu bổ sung, điều\r\nchỉnh mà không có điều kiện trực tiếp đến </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">thực hiện\r\nthủ tục</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">. Nếu người\r\nđược ủy quyền là ông, bà, cha, mẹ, con, vợ, chồng, anh, chị, em ruột của người\r\nủy quyền, thì không cần phải có văn bản ủy quyền, nhưng phải có giấy tờ chứng\r\nminh về mối quan hệ nêu trên.</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">b) Số bộ hồ sơ:</span></i></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> 01 bộ.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Thời hạn giải quyết: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Trong thời hạn 03 ngày làm việc kể từ khi nhận đủ hồ sơ\r\nhợp lệ;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Trong trường </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">hợp\r\ncần phải xác minh, thì thời hạn nói trên được kéo dài thêm không quá 05 ngày\r\nlàm việc.</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Đối tượng thực hiện: </b>Cá nhân.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;tab-stops:210.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nNL;mso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"><b>Cơ quan thực\r\nhiện:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Cơ quan có thẩm quyền quyết định: UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp\r\nhuyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Kết quả: </b></span><span style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;\">Quyết định về việc thay đổi, cải chính hộ tịch</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-weight:bold\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Phí, lệ phí:\r\n</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Lệ phí đăng ký thay đổi, cải chính hộ tịch: 25.000đ<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-font-kerning:18.0pt;\r\nmso-ansi-language:NL;mso-bidi-font-weight:bold\">(</span></i><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007)<o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Tên mẫu đơn, mẫu tờ khai: </b>Tờ khai đăng\r\nký lại việc thay đổi, cải chính, bổ sung hộ tịch xác định lại dân tộc, xác định\r\nlại giới tính trong giấy khai sinh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">(Thông\r\ntư số 05/2012/TT-BTP ngày 23/5/2012 của Bộ Tư pháp)<b><o:p></o:p></b></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Yêu cầu, điều kiện: </span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Không<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Căn cứ pháp lý<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị định số 158/2005/NĐ-CP ngày 27/12/2005 của Chính\r\nphủ về đăng ký quản lý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị định số 06/2012/NĐ-CP ngày 02/02/2012 của Chính\r\nphủ về sửa đổi, bổ sung một số điều của các Nghị định về hộ tịch, hôn nhân gia\r\nđình và chứng thực;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thông tư số 01/2008/TT-BTP ngày 02/6/2008 của Bộ Tư\r\npháp Hướng dẫn thực hiện một số quy định của Nghị định số 158/2005/NĐ-CP ngày\r\n27/12/2005 của Chính phủ về đăng ký và quản lý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thông tư số 05/2012/TT-BTP ngày 23/5/2012 của Bộ Tư\r\npháp về Sửa đổi, bổ sung một số điều của Thông tư số 08.a/2010/TT-BTP ngày 25\r\ntháng 3 năm 2010 của Bộ Tư pháp về việc ban hành và hướng dẫn việc ghi chép,\r\nlưu trữ, sử dụng sổ, biểu mẫu hộ tịch;<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007 của Hội\r\nđồng nhân dân tỉnh Quy định mức thu và tỷ lệ điều tiết các loại phí, lệ phí của\r\ntỉnh Bắc Giang.<o:p></o:p></span></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (109,'TPH05A','Thủ tục Xác định lại dân tộc',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Trình tự thực hiện</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n1:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Người\r\nyêu cầu </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Xác định lại dân tộc</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">\r\ncó thể nộp trực tiếp hoặc gửi hồ sơ qua hệ thống bưu chính</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> đến Bộ phận\r\ntiếp nhận và trả kết quả - UBND cấp huyện. Cán bộ kiểm tra và tiếp nhận hồ sơ,\r\ncông dân nộp lệ phí xác định lại dân tộc.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thời gian nhận hồ sơ: Trong giờ hành chính từ thứ 2 đến\r\nthứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><b><span lang=\"NL\" style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước 2:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nếu việc </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Xác định lại\r\ndân tộc</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> có đủ điều\r\nkiện theo quy định của pháp luật, cán bộ Tư pháp của Phòng Tư pháp ghi vào Sổ\r\nđã đăng ký khai sinh trước đây và Quyết định về việc </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Xác định lại\r\ndân tộc</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">. Chủ tịch Ủy\r\nban nhân dân cấp huyện ký và cấp cho đương sự một bản chính Quyết định về việc </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Xác\r\nđịnh lại dân tộc</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">.\r\nBản sao Quyết định được cấp theo yêu cầu của đương sự.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nội dung và căn cứ </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Xác\r\nđịnh lại dân tộc</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">\r\nphải được ghi chú vào cột ghi những thay đổi sau này của Sổ đăng ký khai sinh\r\nvà mặt sau của bản chính Giấy khai sinh.</span><span lang=\"NL\" style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n3: </span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Sau khi thực hiện việc xác định lại dân tộc thì Ủy ban\r\nnhân dân cấp huyện có trách nhiệm gửi thông báo cho Ủy ban nhân dân cấp xã, nơi\r\nđã đăng ký hộ tịch về những nội dung thay đổi để ghi tiếp vào sổ hộ tịch lưu\r\ntại Ủy ban nhân dân cấp xã.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Bước\r\n4:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"> Công dân nhận kết quả tại Bộ phận tiếp nhận và trả kết\r\nquả - UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2\r\nđến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Cách thức thực hiện<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Thành phần, số lượng hồ sơ<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:5.65pt;margin-bottom:\r\n6.0pt;margin-left:-3.1pt;text-align:justify;mso-pagination:none;mso-layout-grid-align:\r\nnone;text-autospace:none\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">&nbsp;a) Thành phần hồ\r\nsơ: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">1- Tờ khai (theo mẫu quy định); x</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">ác định lại dân tộc cho người\r\nchưa thành niên từ đủ 15 tuổi trở lên, thì phải có ý kiến đồng ý của người đó\r\nthể hiện trong Tờ khai.</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">2- </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Xuất\r\ntrình bản chính Giấy khai sinh của người cần </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Xác định lại\r\ndân tộc</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> và các giấy\r\ntờ liên quan để làm căn cứ cho việc </span><span lang=\"NL\" style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Xác định lại dân tộc</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-\r\nTrường hợp hồ sơ nộp trực tiếp thì nộp bản sao kèm bản chính để đối chiếu hoặc\r\nbản sao có chứng thực.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-\r\nTrường hợp gửi qua hệ thống bưu chính, thì các giấy tờ có trong thành phần hồ\r\nsơ phải là bản sao có chứng thực.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">3- Xuất trình </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Giấy\r\nchứng minh nhân dân hoặc Hộ chiếu của người đi đăng ký hộ tịch để xác định về\r\ncá nhân người đó.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4-\r\nXuất trình Sổ hộ khẩu, Sổ đăng ký tạm trú để làm căn cứ xác định thẩm quyền\r\nđăng ký hộ tịch.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">5.\r\nVăn bản thỏa thuận của cha mẹ về việc xác định lại dân tộc cho con trong trường\r\nhợp xác định lại dân tộc cho con dưới 15 tuổi.</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.3pt;mso-ansi-language:NL\">6- </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Văn bản uỷ\r\nquyền được công chứng hoặc chứng thực trong trường hợp </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">người có yêu cầu bổ sung, điều\r\nchỉnh mà không có điều kiện trực tiếp đến </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">thực hiện\r\nthủ tục</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">. Nếu người\r\nđược ủy quyền là ông, bà, cha, mẹ, con, vợ, chồng, anh, chị, em ruột của người\r\nủy quyền, thì không cần phải có văn bản ủy quyền, nhưng phải có giấy tờ chứng minh\r\nvề mối quan hệ nêu trên</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">b) Số bộ hồ sơ:</span></i></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> 01 bộ.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Thời hạn giải quyết: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Trong thời hạn 03 ngày làm việc kể từ khi nhận đủ hồ sơ\r\nhợp lệ;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Trong trường </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">hợp\r\ncần phải xác minh, thì thời hạn nói trên được kéo dài thêm không quá 05 ngày\r\nlàm việc.</span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Đối tượng thực hiện: </b>Cá nhân<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;tab-stops:210.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nNL;mso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\"><b>Cơ quan thực hiện&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Cơ quan có thẩm quyền quyết định: UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp\r\nhuyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Kết quả: </b></span><span style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;\">Quyết định về việc </span><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Xác định lại\r\ndân tộc.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Phí, lệ phí:\r\n<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Lệ phí đăng ký Xác định lại dân tộc: 25.000đ<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-font-kerning:18.0pt;\r\nmso-ansi-language:NL;mso-bidi-font-weight:bold\">(</span></i><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007)<o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"><b>Tên mẫu đơn, mẫu tờ khai: </b><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Tờ khai đăng ký việc thay đổi, cải chính, bổ sung hộ\r\ntịch xác định lại dân tộc, xác định lại giới tính trong giấy khai sinh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">(Thông\r\ntư số 05/2012/TT-BTP ngày 23/5/2012 của Bộ Tư pháp)<b><o:p></o:p></b></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Yêu cầu, điều kiện: </span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Không<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\"></span></b><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Căn cứ pháp lý<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị định số 158/2005/NĐ-CP ngày 27/12/2005 của Chính\r\nphủ về đăng ký quản lý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị định số 06/2012/NĐ-CP ngày 02/02/2012 của Chính\r\nphủ về sửa đổi, bổ sung một số điều của các Nghị định về hộ tịch, hôn nhân gia\r\nđình và chứng thực;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thông tư số 01/2008/TT-BTP ngày 02/6/2008 của Bộ Tư\r\npháp Hướng dẫn thực hiện một số quy định của Nghị định số 158/2005/NĐ-CP ngày\r\n27/12/2005 của Chính phủ về đăng ký và quản lý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Thông tư số 05/2012/TT-BTP ngày 23/5/2012 của Bộ Tư\r\npháp về Sửa đổi, bổ sung một số điều của Thông tư số 08.a/2010/TT-BTP ngày 25\r\ntháng 3 năm 2010 của Bộ Tư pháp về việc ban hành và hướng dẫn việc ghi chép,\r\nlưu trữ, sử dụng sổ, biểu mẫu hộ tịch;<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">- Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007 của Hội\r\nđồng nhân dân tỉnh Quy định mức thu và tỷ lệ điều tiết các loại phí, lệ phí của\r\ntỉnh Bắc Giang.<o:p></o:p></span></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (110,'TPH06A','Thủ tục Xác định lại giới tính',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Trình tự thực hiện</span></b><span lang=\"NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\">Bước 1:</span></b><span lang=\"NL\"> </span>Người yêu cầu <span lang=\"NL\">Xác định lại giới tính</span> có thể nộp trực tiếp\r\nhoặc gửi hồ sơ qua hệ thống bưu chính<span lang=\"NL\"> đến Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện. Cán bộ kiểm tra và\r\ntiếp nhận hồ sơ, công dân nộp lệ phí xác định lại giới tính.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thời gian nhận hồ sơ: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><b><span lang=\"NL\">Bước 2:</span></b><span lang=\"NL\"> </span>Nếu\r\nviệc <span lang=\"NL\">Xác định lại giới tính</span>\r\ncó đủ điều kiện theo quy định của pháp luật, cán bộ Tư pháp của Phòng Tư pháp\r\nghi vào Sổ đã đăng ký khai sinh trước đây và Quyết định về việc <span lang=\"NL\">Xác định lại giới tính</span>. Chủ tịch Ủy ban\r\nnhân dân cấp huyện ký và cấp cho đương sự một bản chính Quyết định về việc <span lang=\"NL\">Xác định lại giới tính</span>. Bản sao\r\nQuyết định được cấp theo yêu cầu của đương sự.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\">Nội dung và căn <span lang=\"NL\">Xác định lại\r\ngiới tính</span> phải được ghi chú vào cột ghi những thay đổi sau này của Sổ\r\nđăng ký khai sinh và mặt sau của bản chính Giấy khai sinh.<span lang=\"NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\">Bước 3: </span></b><span lang=\"NL\">Sau khi thực hiện việc xác định lại giới tính thì\r\nỦy ban nhân dân cấp huyện có trách nhiệm gửi thông báo cho Ủy ban nhân dân cấp\r\nxã, nơi đã đăng ký hộ tịch về những nội dung thay đổi để ghi tiếp vào sổ hộ\r\ntịch lưu tại Ủy ban nhân dân cấp xã.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\">Bước 4:</span></b><span lang=\"NL\"> Công dân nhận kết quả tại Bộ phận tiếp nhận và\r\ntrả kết quả - UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Cách thức thực hiện: </b></span>Nộp trực\r\ntiếp hoặc gửi hồ sơ qua hệ thống bưu chính<span lang=\"NL\"> đến Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện.<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Thành phần, số lượng hồ sơ<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:5.65pt;margin-bottom:\r\n6.0pt;margin-left:-3.1pt;text-align:justify;mso-pagination:none;mso-layout-grid-align:\r\nnone;text-autospace:none\"><b><i><span lang=\"NL\">&nbsp;a) Thành phần hồ sơ: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">1- Tờ khai (theo mẫu quy định);<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">2- </span>Xuất trình bản chính Giấy khai sinh của người cần <span lang=\"NL\">Xác định lại giới tính</span> và các giấy tờ liên\r\nquan để làm căn cứ cho việc <span lang=\"NL\">Xác định\r\nlại giới tính</span>.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">- Trong trường hợp gửi qua hệ thống bưu\r\nchính, thì các giấy tờ có trong thành phần hồ sơ phải là bản sao có chứng thực<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">- Trường hợp trực tiếp thì nộp bản sao kèm\r\nbản chính để đối chiếu hoặc bản sao có chứng thực;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">3- </span>Giấy chứng nhận y tế do Cơ sở khám bệnh, chữa bệnh được phép can\r\nthiệp y tế để xác định lại giới tính theo quy định của Nghị định số\r\n88/2008/NĐ-CP ngày 05 tháng 8 năm 2008 của Chính phủ về xác định lại giới tính.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">4- Xuất trình Sổ hộ khẩu, Sổ đăng ký tạm\r\ntrú để làm căn cứ xác định thẩm quyền đăng ký hộ tịch.<span lang=\"NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\" style=\"letter-spacing:-.3pt;\r\nmso-ansi-language:NL\">5- </span><span lang=\"NL\">Văn\r\nbản uỷ quyền được công chứng hoặc chứng thực trong trường hợp </span>người có\r\nyêu cầu bổ sung, điều chỉnh mà không có điều kiện trực tiếp đến <span lang=\"NL\">thực hiện thủ tục</span>. Nếu người được ủy quyền\r\nlà ông, bà, cha, mẹ, con, vợ, chồng, anh, chị, em ruột của người ủy quyền, thì\r\nkhông cần phải có văn bản ủy quyền, nhưng phải có giấy tờ chứng minh về mối\r\nquan hệ nêu trên.<span lang=\"NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><i><span lang=\"NL\">b)\r\nSố bộ hồ sơ:</span></i></b><span lang=\"NL\"> 01 bộ.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Thời hạn giải quyết: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Trong thời hạn 03 ngày làm việc kể từ khi nhận đủ hồ sơ hợp lệ;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Trong trường </span>hợp cần phải xác minh, thì thời hạn nói trên được kéo\r\ndài thêm không quá 05 ngày làm việc.<span lang=\"NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Đối tượng thực hiện: </b>Cá nhân<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;tab-stops:210.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Cơ quan thực hiện&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in\"><span lang=\"NL\">- Cơ quan có thẩm\r\nquyền quyết định: UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Kết quả: </b></span>Quyết định về việc <span lang=\"NL\">Xác định lại giới tính.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Phí, lệ phí: </span></b><span lang=\"NL\">Lệ phí\r\nđăng ký Xác định lại giới tính: 25.000đ<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\">(</span></i><i><span lang=\"NL\">Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007)<o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Tên mẫu đơn, mẫu tờ khai: </b>Tờ khai đăng\r\nký lại việc thay đổi, cải chính, bổ sung hộ tịch xác định lại dân tộc, xác định\r\nlại giới tính trong giấy khai sinh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\">(Thông tư số 05/2012/TT-BTP ngày 23/5/2012\r\ncủa Bộ Tư pháp)<b><o:p></o:p></b></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Yêu cầu, điều kiện: </span></b><span lang=\"NL\">Không<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Căn cứ pháp lý<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Nghị định số 158/2005/NĐ-CP ngày 27/12/2005 của Chính phủ về đăng ký quản\r\nlý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Nghị định số 06/2012/NĐ-CP ngày 02/02/2012 của Chính phủ về sửa đổi, bổ\r\nsung một số điều của các Nghị định về hộ tịch, hôn nhân gia đình và chứng thực;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thông tư số 01/2008/TT-BTP ngày 02/6/2008 của Bộ Tư pháp Hướng dẫn thực\r\nhiện một số quy định của Nghị định số 158/2005/NĐ-CP ngày 27/12/2005 của Chính\r\nphủ về đăng ký và quản lý hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thông tư số 05/2012/TT-BTP ngày 23/5/2012 của Bộ Tư pháp về Sửa đổi, bổ\r\nsung một số điều của Thông tư số 08.a/2010/TT-BTP ngày 25 tháng 3 năm 2010 của\r\nBộ Tư pháp về việc ban hành và hướng dẫn việc ghi chép, lưu trữ, sử dụng sổ,\r\nbiểu mẫu hộ tịch;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007 của Hội đồng nhân dân tỉnh\r\nQuy định mức thu và tỷ lệ điều tiết các loại phí, lệ phí của tỉnh Bắc Giang.</span><b><o:p></o:p></b></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (111,'TPH07A','Thủ tục Chứng thực bản sao từ bản chính',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;line-height:15.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span>Trình tự thực hiện</b><u1:p></u1:p><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 1:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span>Người yêu cầu<span class=\"apple-converted-space\">&nbsp;</span><span lang=\"NL\">chứng thực</span><span class=\"apple-converted-space\">&nbsp;</span>nộp hồ sơ tại<span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Bộ phận tiếp nhận và trả kết quả -\r\nUBND cấp huyện. Cán bộ kiểm tra và tiếp nhận hồ sơ, công dân nộp lệ phí chứng\r\nthực.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Thời gian nhận hồ sơ: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 2:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Người thực hiện chứng thực phải kiểm\r\ntra tính hợp pháp của bản chính, nếu phát hiện bản chính có dấu hiệu giả mạo\r\nthì đề nghị người yêu cầu chứng thực chứng minh; nếu không chứng minh được thì\r\ntừ chối chứng thực.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 3:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Người thực hiện chứng thực đối chiếu\r\nbản sao với bản chính, nếu bản sao đúng với bản chính thì chứng thực. Khi chứng\r\nthực bản sao từ bản chính người thực hiện chứng thực phải ghi rõ “chứng thực\r\nbản sao đúng với bản chính”, ngày, tháng, năm chứng thực, ký, ghi rõ họ tên và\r\nđóng dấu của cơ quan có thẩm quyền chứng thực.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">Trang đầu tiên của bản sao phải được ghi rõ chữ \"BẢN SAO\" vào chỗ\r\ntrống phía trên bên phải, nếu bản sao có từ hai tờ trở lên thì phải đóng dấu\r\ngiáp lai.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 4:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Công dân nhận kết quả tại Bộ phận tiếp\r\nnhận và trả kết quả - UBND cấp huyện.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Cách thức thực hiện</span></b><b><u1:p></u1:p></b><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Thành phần, số lượng hồ sơ</span></b><b><u1:p></u1:p></b><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 5.65pt 6pt -3.1pt; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">&nbsp;<i>a)\r\nThành phần hồ sơ:<u1:p></u1:p></i></span></b><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">1- Bản chính;<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">2- Bản sao cần chứng thực</span>.<u1:p></u1:p><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\">b) Số bộ hồ sơ:</span></i></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Cá nhân, tổ chức chuẩn bị số lượng bản sao theo nhu cầu.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span>Thời hạn giải quyết:<u1:p></u1:p></b><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">Việc tiếp nhận yêu cầu chứng thực bản sao từ bản chính trong thời gian làm\r\nviệc buổi sáng hoặc buổi chiều thì phải được thực hiện chứng thực ngay trong\r\nbuổi làm việc đó; trường hợp yêu cầu chứng thực với số lượng lớn thì việc chứng\r\nthực có thể được hẹn lại để chứng thực sau nhưng không quá 2 ngày làm việc.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Đối tượng thực hiện:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">Cá nhân, tổ chức<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Cơ quan thực\r\nhiện&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></b><b><u1:p></u1:p></b><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan có thẩm quyền quyết định: Phòng Tư pháp cấp huyện.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp huyện.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan phối hợp:<span class=\"apple-converted-space\">&nbsp;</span></span>Văn phòng HĐND và UBND\r\ncấp huyện.<u1:p></u1:p><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Kết quả:<span class=\"apple-converted-space\">&nbsp;</span></span></b>Bản sao<span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">có chứng thực.<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Phí, lệ phí:<u1:p></u1:p></span></b><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">Chứng thực bản sao từ bản chính: không quá 2000 đồng/trang; từ trang thứ 3\r\ntrở lên thì mỗi trang thu không quá 1.000 đồng/trang, tối đa thu không quá\r\n100.000 đồng/bản<span class=\"apple-converted-space\"><i>&nbsp;</i></span><i>(Nghị\r\nquyết số 14/2009/NQ-HĐND ngày 09/12/2009 của Hội đồng nhân dân tỉnh)</i></span><u1:p></u1:p><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Tên mẫu đơn, mẫu tờ khai:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">&nbsp;Không<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span>Yêu cầu, điều kiện:<span class=\"apple-converted-space\">&nbsp;</span></b><span lang=\"NL\">Không<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Căn cứ pháp lý<u1:p></u1:p></span></b><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span style=\"letter-spacing: -0.2pt;\">-\r\nNghị định số 79/2007/NĐ-CP ngày 18/5/2007 của Chính phủ về cấp bản sao từ sổ\r\ngốc, chứng thực bản sao từ bản chính, chứng thực chữ ký;<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">- Nghị định số\r\n04/2012/NĐ-CP ngày 20/01/2012 của Chính phủ sửa đổi, bổ sung Điều 5 của Nghị\r\nđịnh số 79/2007/NĐ-CP ngày 18/5/2007 của Chính phủ về cấp bản sao từ sổ gốc,\r\nchứng thực bản sao từ bản chính, chứng thực chữ ký;<u1:p></u1:p><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">- Thông tư số\r\n03/2008/TT-BTP ngày 25/8/2005 của Bộ Tư pháp hướng dẫn thi hành một số điều của\r\nNghị định số 79/2007/NĐ-CP<span class=\"apple-converted-space\"><span style=\"letter-spacing:-.2pt\">&nbsp;</span></span><span style=\"letter-spacing:-.2pt\">ngày 18/5/2007 của Chính phủ về cấp bản sao từ sổ\r\ngốc, chứng thực bản sao từ bản chính, chứng thực chữ ký</span>;<u1:p></u1:p><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Thông tư liên tịch số 92/2008/TTLT-BTC-BTP ngày 17/10/2008 của Bô Tài\r\nchính, Bộ Tư pháp<span class=\"apple-converted-space\">&nbsp;</span>Hướng dẫn về\r\nmức thu, chế độ thu, nộp, quản lý và sử dụng lệ phí cấp bản sao, lệ phí chứng\r\nthực;<u1:p></u1:p></span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p style=\"margin: 0in 0in 7.5pt; line-height: 15pt;\"><span lang=\"NL\">- Nghị quyết số 14/2009/NQ-HĐND ngày 09/12/2009 của Hội đồng nhân dân tỉnh\r\nsửa đổi, bổ sung, thay thế một số loại phí, lệ phí ban hành kèm theo nghị quyết\r\nsố 11/2007/NQ-HĐND ngày 19/72007 của Hội đồng nhân dân tỉnh khóa XVI.</span><span style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (112,'TPH08A','Thủ tục Chứng thực chữ ký người dịch trong các giấy tờ, văn bản từ tiếng nước ngoài sang tiếng Việt hoặc từ tiếng Việt sang tiếng nước ngoài; chứng thực chữ ký',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Trình tự thực hiện</span></b><span lang=\"NL\">:<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\">Bước 1:</span></b><span lang=\"NL\"> </span>Người yêu cầu <span lang=\"NL\">chứng thực</span> nộp hồ sơ tại<span lang=\"NL\"> Bộ phận tiếp nhận và trả kết quả - UBND cấp\r\nhuyện. Cán bộ kiểm tra và tiếp nhận hồ sơ, công dân nộp lệ phí chứng thực.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thời gian nhận hồ sơ: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><b><span lang=\"NL\">Bước 2:</span></b><span lang=\"NL\"> <span style=\"letter-spacing:-.3pt\">Người yêu cầu chứng thực phải ký trước mặt người\r\nthực hiện chứng thực.</span> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;mso-layout-grid-align:none;text-autospace:\r\nnone\"><b><span lang=\"NL\">Bước 3:</span></b><span lang=\"NL\"> Người thực\r\nhiện chứng thực phải ghi rõ ngày, tháng, năm chứng thực; địa điểm chứng thực;\r\nsố giấy tờ tuỳ thân của người yêu cầu chứng thực, ngày cấp, nơi cấp; chữ ký\r\ntrong giấy tờ, văn bản đúng là chữ ký của người yêu cầu chứng thực; sau đó ký\r\nvà ghi rõ họ, tên và đóng dấu của cơ quan có thẩm quyền chứng thực.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><span lang=\"NL\">Bước 4:</span></b><span lang=\"NL\"> Công dân nhận kết quả tại Bộ phận tiếp nhận và\r\ntrả kết quả - UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Cách thức thực hiện:<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Thành phần, số lượng hồ sơ<o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:5.65pt;margin-bottom:\r\n6.0pt;margin-left:-3.1pt;text-align:justify;mso-pagination:none;mso-layout-grid-align:\r\nnone;text-autospace:none\"><b><i><span lang=\"NL\">&nbsp;a) Thành phần hồ sơ: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">1- Xuất trình Chứng minh nhân dân hoặc hộ chiếu hoặc giấy tờ tuỳ thân khác\r\nđể cán bộ tiếp nhận hồ sơ kiểm tra;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">2- Giấy tờ, văn bản mà mình sẽ ký vào đó</span>.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><b><i><span lang=\"NL\">b)\r\nSố bộ hồ sơ:</span></i></b><span lang=\"NL\"> Theo nhu\r\ncầu của cá nhân.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Thời hạn giải quyết: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">Việc tiếp nhận yêu cầu chứng thực chữ ký trong thời gian làm việc buổi sáng\r\nhoặc buổi chiều thì phải được thực hiện chứng thực ngay trong buổi làm việc đó;\r\ntrường hợp cần phải xác minh làm rõ nhân thân của người yêu cầu chứng thực thì\r\nthời hạn trên được kéo dài thêm nhưng không được quá 03 ngày làm việc.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Đối tượng thực hiện: </b>Cá nhân<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;tab-stops:210.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Cơ quan thực hiện:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in\"><span lang=\"NL\">- Cơ quan có thẩm\r\nquyền quyết định: Phòng Tư pháp cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Cơ quan phối hợp: </span>Văn phòng HĐND và UBND cấp huyện.<span lang=\"NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Kết quả: </b>Giấy tờ, văn bản.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Phí, lệ phí: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">Lệ phí chứng thực: 10.000 đồng/trường hợp<i> <o:p></o:p></i></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i><span lang=\"NL\">(</span></i><i><span lang=\"NL\">Nghị quyết số 14/2009/NQ-HĐND ngày 09/12/2009 của\r\nHội đồng nhân dân tỉnh)</span></i><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span lang=\"NL\"><b>Tên mẫu đơn, mẫu tờ khai: </b>&nbsp;Không<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Yêu cầu, điều kiện: </span></b><span lang=\"NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">Đối với người dịch:<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">Người dịch phải là người thông thạo tiếng nước ngoài cần dịch (người dịch\r\ncó bằng cử nhân ngoại ngữ trở lên về thứ tiếng nước ngoài cần dịch hoặc người\r\ndịch có bằng tốt nghiệp cao đẳng trở lên tại nước ngoài đối với thứ tiếng nước\r\nngoài cần dịch).<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><i>(Thông\r\ntư số 03/2008/TT-BTP ngày 25/8/2005 của Bộ Tư pháp) </i><i><span lang=\"NL\"><o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><b><span lang=\"NL\">Căn cứ pháp lý<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span style=\"letter-spacing:-.2pt\">- Nghị\r\nđịnh số 79/2007/NĐ-CP ngày 18/5/2007 của Chính phủ về cấp bản sao từ sổ gốc,\r\nchứng thực bản sao từ bản chính, chứng thực chữ ký;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">- Nghị định số 04/2012/NĐ-CP ngày\r\n20/01/2012 của Chính phủ sửa đổi, bổ sung Điều 5 của Nghị định số 79/2007/NĐ-CP\r\nngày 18/5/2007 của Chính phủ về cấp bản sao từ sổ gốc, chứng thực bản sao từ\r\nbản chính, chứng thực chữ ký;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">- Thông tư số 03/2008/TT-BTP ngày 25/8/2005\r\ncủa Bộ Tư pháp hướng dẫn thi hành một số điều của Nghị định số 79/2007/NĐ-CP<span style=\"letter-spacing:-.2pt\"> ngày 18/5/2007 của Chính phủ về cấp bản sao từ sổ\r\ngốc, chứng thực bản sao từ bản chính, chứng thực chữ ký</span>;<span lang=\"NL\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Thông tư liên tịch số 92/2008/TTLT-BTC-BTP ngày 17/10/2008 của Bô Tài\r\nchính, Bộ Tư pháp Hướng dẫn về mức thu,\r\nchế độ thu, nộp, quản lý và sử dụng lệ phí cấp bản sao, lệ phí chứng thực;<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\"><span lang=\"NL\">- Nghị quyết số 14/2009/NQ-HĐND ngày 09/12/2009 của Hội đồng nhân dân tỉnh\r\nsửa đổi, bổ sung, thay thế một số loại phí, lệ phí ban hành kèm theo nghị quyết\r\nsố 11/2007/NQ-HĐND ngày 19/72007 của Hội đồng nhân dân tỉnh khóa XVI.</span><b><o:p></o:p></b></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (113,'TPH09A','Thủ tục Chứng thực hợp đồng, giao dịch liên quan đến động sản có giá trị dưới 50 triệu đồng',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;line-height:15.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span>Trình tự thực hiện</b><span lang=\"NL\">:<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 1:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span>Người yêu cầu<span class=\"apple-converted-space\">&nbsp;</span><span lang=\"NL\">chứng thực</span><span class=\"apple-converted-space\">&nbsp;</span>nộp hồ sơ tại<span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Bộ phận tiếp nhận và trả kết quả -\r\nUBND cấp huyện. Cán bộ kiểm tra và tiếp nhận hồ sơ,</span><span class=\"apple-converted-space\">&nbsp;</span>ghi vào sổ,<span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">công dân nộp lệ phí chứng thực.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Thời gian nhận hồ sơ: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 2:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span><span lang=\"NL\" style=\"letter-spacing: -0.3pt;\">Người yêu\r\ncầu chứng thực phải ký trước mặt người thực hiện chứng thực (</span></span>ký tắt vào từng trang\r\ncủa hợp đồng, trừ trang cuối phải ký đầy đủ).<o:p></o:p><u1:p></u1:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 3:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Người thực hiện chứng thực phải ghi rõ\r\nl</span>ời\r\nchứng của người thực hiện chứng thực: thời điểm giao kết hợp đồng, địa điểm\r\nchứng thực, năng lực hành vi dân sự, chữ ký của các bên và nội dung thoả thuận\r\ncủa các bên<span lang=\"NL\">, sau đó ký và ghi rõ họ, tên và đóng\r\ndấu của cơ quan có thẩm quyền chứng thực.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 4:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Công dân nhận kết quả tại Bộ phận tiếp\r\nnhận và trả kết quả - UBND cấp huyện.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Cách thức thực hiện:</span></b><b><u1:p></u1:p></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Thành phần, số lượng hồ sơ</span></b><b><u1:p></u1:p></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 5.65pt 6pt -3.1pt; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\">&nbsp;a) Thành phần hồ sơ:<u1:p></u1:p></span></i></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">1-<span class=\"apple-converted-space\">&nbsp;</span></span>Phiếu yêu cầu chứng\r\nthực hợp đồng, văn bản;<o:p></o:p><u1:p></u1:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">2-<span class=\"apple-converted-space\">&nbsp;</span></span>Xuất trình giấy tờ tuỳ\r\nthân và giấy tờ cần thiết để thực hiện việc công chứng, chứng thực;<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">3- Trong trường hợp\r\nhợp đồng liên quan đến tài sản mà pháp luật quy định phải đăng ký quyền sở hữu,\r\nquyền sử dụng, thì người yêu cầu công chứng, chứng thực phải có đầy đủ giấy tờ\r\nđể chứng minh quyền sở hữu.<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\">b) Số bộ hồ sơ:</span></i></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">01 bộ.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span>Thời hạn giải quyết:<u1:p></u1:p></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">Việc tiếp nhận yêu cầu chứng thực chữ ký trong thời gian làm việc buổi sáng\r\nhoặc buổi chiều thì phải được thực hiện chứng thực ngay trong buổi làm việc đó;\r\ntrường hợp cần phải xác minh làm rõ nhân thân của người yêu cầu chứng thực thì\r\nthời hạn trên được kéo dài thêm nhưng không được quá 03 ngày làm việc.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Đối tượng thực hiện:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">Tổ chức, cá nhân<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Cơ quan thực\r\nhiện:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></b><b><u1:p></u1:p></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan có thẩm quyền quyết định: Phòng Tư pháp cấp huyện.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp huyện.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan phối hợp:<span class=\"apple-converted-space\">&nbsp;</span></span>Văn phòng HĐND và UBND\r\ncấp huyện.<o:p></o:p><u1:p></u1:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Kết quả:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">Hợp đồng, giao dịch.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Phí, lệ phí:<u1:p></u1:p></span></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">Lệ phí chứng thực:<span class=\"apple-converted-space\">&nbsp;</span>40.000\r\nđồng/trường hợp.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><i><span lang=\"NL\">(</span>Thông tư liên tịch số 62/2013/TTLT-BTC-BTP ngày\r\n13/5/2013 của Bộ Tư pháp- Bộ Tài chính)</i><u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Tên mẫu đơn, mẫu tờ khai:<span class=\"apple-converted-space\">&nbsp;</span></span></b>Phiếu yêu cầu chứng\r\nthực hợp đồng, văn bản<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><i><span style=\"letter-spacing: -0.2pt;\">(Thông tư 03/2001/TP-CC ngày 14/3/2001\r\ncủa Bộ Tư pháp)</span></i><u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span>Yêu cầu, điều kiện:</b><u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">Nội dung hợp đồng\r\nkhông trái pháp luật, đạo đức xã hội.<o:p></o:p><u1:p></u1:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><i>(</i><i><span lang=\"NL\">Nghị định số 75/2000/NĐ-CP ngày 08/12/2000 của Chính phủ về công chứng,\r\nchứng thực</span>)</i><u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Căn cứ pháp lý<u1:p></u1:p></span></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span style=\"letter-spacing: -0.2pt;\">-<span class=\"apple-converted-space\">&nbsp;</span></span>Bộ Luật Dân sự năm 2005;<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span style=\"letter-spacing: -0.2pt;\">-<span class=\"apple-converted-space\">&nbsp;</span></span><span lang=\"NL\">Nghị định số 75/2000/NĐ-CP\r\nngày 08/12/2000 của Chính phủ về công chứng, chứng thực</span><span style=\"letter-spacing: -0.2pt;\">;<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span style=\"letter-spacing: -0.2pt;\">-\r\nThông tư 03/2001/TP-CC ngày 14/3/2001 của Bộ Tư pháp hướng dẫn thi hành<span class=\"apple-converted-space\">&nbsp;</span></span><span lang=\"NL\">Nghị định số 75/2000/NĐ-CP\r\nngày 08/12/2000 của Chính phủ về công chứng, chứng thực;<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify\">\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<u1:p></u1:p>\r\n\r\n\r\n\r\n\r\n\r\n</p><p style=\"margin: 0in 0in 7.5pt; line-height: 15pt;\">-<span class=\"apple-converted-space\">&nbsp;</span><span lang=\"NL\">Thông tư liên tịch số\r\n62/2013/TTLT-BTC-BTP ngày 13/5/2013 của Bộ Tư pháp- Bộ Tài chính quy định mức\r\nthu, chế độ thu, nộp, quản lý và sử dụng lệ phí chứng thực hợp đồng giao dịch.</span><o:p></o:p></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (114,'KHH13A','Cấp lại Giấy chứng nhận Đăng ký kinh doanh của hộ cá thể',15,NULL,3,'<p>16.1. Trình tự thực hiện:&nbsp;</p><p>Bước 1: Cá nhân nộp hồ sơ tại Bộ phận tiếp nhận và trả kết quả của UBND huyện; cán bộ tiếp nhận hướng dẫn Cá nhân viết đơn đề nghị cấp lại Giấy chứng nhận đăng ký kinh doanh, kiểm tra hồ sơ và viết giấy hẹn.&nbsp;</p><p>Bước 2: &nbsp;Hồ sơ được chuyển tới Phòng Tài chính - Kế hoạch để thẩm định và hoàn thiện giấy chứng nhận đăng ký kinh doanh.</p><p>Bước 3: Phòng Tài chính - Kế hoạch trình Chủ tịch ubND huyện xem xét và ký giấy chứng nhận đăng ký kinh doanh; hồ sơ được chuyển lại Bộ phận tiếp nhận và trả kết quả.</p><p>Bước 4: Cá nhân xuất trình giấy hẹn trả kết quả, nộp lệ phí và nhận Giấy chứng nhận kinh doanh tại Bộ phận tiếp nhận và trả kết quả của UBND huyện</p><p>16.2. Cách thức thực hiện: &nbsp;Trực tiếp tại Bộ phận tiếp nhận và trả kết quả của UBND huyện</p><p>16.3. Hồ sơ:</p><p>a) Thành phần hồ sơ bao gồm:</p><p>1- Đơn Đăng ký kinh doanh (nội dung xin cấp lại)</p><p>b) Số lượng hồ sơ: &nbsp; &nbsp;01 (bộ)</p><p>16.4. Thời hạn giải quyết: &nbsp;05 ngày làm việc kể từ ngày nhận đủ hồ sơ</p><p>16.5. Cơ quan thực hiện:</p><p>a) Cơ quan có thẩm quyền quyết định: Chủ tịch UBND huyện</p><p>b) Cơ quan trực tiếp thực hiện: &nbsp;Phòng Tài chính – Kế hoạch.</p><p>16.6. Đối tượng thực hiện: &nbsp;Tổ chức &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>16.7. Mẫu đơn, tờ khai: Không</p><p>16.8. Phí, lệ phí: Lệ phí cấp lại đăng ký kinh doanh: 100.000 đồng</p><p>( Thông tư số 176/2012/TT-BTC ngày 23/12/2012 của Bộ Tài chính)</p><p>16.9. Kết quả: Giấy chứng nhận &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>16.10. yêu cầu, điều kiện: Không</p><p>16.11. Căn cứ pháp lý:</p><p>- Nghị định 43/2010/NĐ-CP ban hành ngày 15/4/2010 của Chính phủ &nbsp;về đăng ký doanh nghiệp;</p><p>- Thông tư số 176/2012/TT-BTC ngày 23/12/2012 của Bộ Tài chính quy định mức thu, chế độ thu, nộp, quản lý và sử dụng lệ phí đăng ký doanh nghiệp, đăng ký hộ kinh doanh và phí cung cấp thông tin doanh nghiệp</p><p>- Nghị định 05/2013/NĐ-CP ban hành ngày 09/01/2013 &nbsp;của Chính phủ &nbsp;hướng dẫn sửa đổi, bổ sung một số điều quy định về thủ tục hành chính của Nghị định 43/2010/NĐ-CP ban hành ngày 15/4/2010 của Chính phủ &nbsp;về đăng ký doanh nghiệp</p><p>-Thông tư số 01/2013/TT-BKH ngày 21/1/2013 của Bộ Kế hoạch và Đầu tư hướng dẫn về hướng dẫn đăng ký doanh nghiệp</p><p>- Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007 của Hội đồng nhân dân tỉnh Bắc Giang về việc Quy định mức thu và tỷ lệ điều tiết các loại phí, lệ phí của tỉnh Bắc Giang.</p><div><br></div>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (115,'KHH14A','Đăng ký thay đổi, bổ sung kinh doanh của hộ cá thể',15,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.1. Trình tự thực\r\nhiện: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước 1: Cá nhân đề nghị cấp giấy\r\nchứng nhận đăng ký kinh doanh nộp hồ sơ tại bộ phận tiếp nhận và trả kết quả\r\ncủa UBND huyện; cán bộ tiếp nhận hướng dẫn Cá nhân viết giấy đề nghị đăng ký\r\nkinh doanh, kiểm tra hồ sơ và viết giấy hẹn.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước 2:&nbsp; Hồ sơ được chuyển tới phòng Tài chính - Kế\r\nhoạch để thẩm định và hoàn thiện giấy chứng nhận đăng ký kinh doanh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước 3: Phòng Tài chính – Kế\r\nhoạch trình lãnh đạo UBND huyện xem xét và ký giấy chứng nhận đăng ký kinh\r\ndoanh; Hồ sơ được chuyển lại Bộ phận tiếp nhận và trả kết quả của UBND huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước 4: Cá nhân xuất trình giấy\r\nhẹn trả kết quả, nộp lệ phí và lấy Giấy chứng nhận kinh doanh tại Bộ phận tiếp\r\nnhận và trả kết quả của UBND huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.2. Cách thức\r\nthực hiện: </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trực\r\ntiếp tại Bộ phận tiếp nhận và trả kết quả của UBND huyện<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.3. Hồ sơ:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a) Thành phần hồ sơ bao gồm:<o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1- Thông báo thay đổi nội dung\r\nđăng ký kinh doanh hộ cá thể;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2- Bản gốc Đăng ký kinh doanh;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">b) Số lượng hồ sơ</span></i></b><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">:</span></i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp; 01&nbsp;&nbsp;\r\n(bộ)<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.4. Thời hạn giải\r\nquyết:&nbsp; </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">05 ngày làm việc kể từ ngày nhận\r\nđủ hồ sơ<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.5. Cơ quan thực\r\nhiện:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a) Cơ quan có thẩm quyền quyết\r\nđịnh: Chủ tịch UBND huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">b) Cơ quan trực tiếp thực hiện:\r\nPhòng Tài chính – Kế hoạch.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.6. Đối tượng\r\nthực hiện:&nbsp; </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Cá nhân&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.7. Mẫu đơn, tờ\r\nkhai: </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Thông báo\r\nthay đổi nội dung đăng ký kinh doanh hộ cá thể<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">(Thông tư số 01/2013/TT-BKH ngày\r\n21/1/2013 của Bộ Kế hoạch và Đầu tư) <b><o:p></o:p></b></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.8. Phí, lệ phí: </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Lệ phí thay đổi đăng ký kinh\r\ndoanh: 100.000 đồng<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">( Thông tư số 176/2012/TT-BTC\r\nngày 23/12/2012 của Bộ Tài chính)<b><o:p></o:p></b></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.9. Kết quả: </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Giấy chứng nhận<b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</b><b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.10. yêu cầu,\r\nđiều kiện: </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Không<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">17.11. Căn cứ pháp\r\nlý:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Nghị định 43/2010/NĐ-CP ban\r\nhành ngày 15/4/2010 của Chính phủ&nbsp; về\r\nđăng ký doanh nghiệp;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Thông tư số 176/2012/TT-BTC\r\nngày 23/12/2012 của Bộ Tài chính quy định mức thu, chế độ thu, nộp, quản lý và\r\nsử dụng lệ phí đăng ký doanh nghiệp, đăng ký hộ kinh doanh và phí cung cấp\r\nthông tin doanh nghiệp<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Nghị định 05/2013/NĐ-CP ban\r\nhành ngày 09/01/2013&nbsp; của Chính phủ&nbsp; hướng dẫn sửa đổi, bổ sung một số điều quy\r\nđịnh về thủ tục hành chính của Nghị định 43/2010/NĐ-CP ban hành ngày 15/4/2010\r\ncủa Chính phủ&nbsp; về đăng ký doanh nghiệp<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-Thông tư số 01/2013/TT-BKH ngày\r\n21/1/2013 của Bộ Kế hoạch và Đầu tư hướng dẫn về hướng dẫn đăng ký doanh nghiệp<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<span style=\"font-size:14.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;mso-ansi-language:EN-US;mso-fareast-language:EN-US;\r\nmso-bidi-language:AR-SA\">- Nghị quyết số 11/2007/NQ-HĐND ngày 19/7/2007 của Hội\r\nđồng nhân dân tỉnh Bắc Giang về việc Quy định mức thu và tỷ lệ điều tiết các\r\nloại phí, lệ phí của tỉnh Bắc Giang</span><br></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (116,'KHH15A','Thông báo tạm ngừng hoạt động hộ kinh doanh cá thể (tự nguyện)',15,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">18.1.\r\nTrình tự thực hiện: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n1: Cá nhân/ đại diện nhóm cá nhân/ đại diện hộ gia đình nộp hồ sơ tại Bộ phận\r\ntiếp nhận và trả kết quả của uBND&nbsp; huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước 2: Bộ phận tiếp nhận và trả\r\nkết quả của UBND&nbsp; huyện tiếp nhận, viết\r\nphiếu hẹn, chuyển hồ sơ cho cán bộ Phòng Tài chính Kế hoạch xem xét, trình UBND\r\ncấp huyện có văn bản thông báo.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">18.2.\r\ncách thức thực hiện:&nbsp; </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tại Bộ phận tiếp nhận và trả kết\r\nquả <span style=\"letter-spacing:-.2pt\">của uBND huyện</span><b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">18.3.\r\nHồ sơ:<o:p></o:p></spa