insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (121,'TPH12A','Thủ tục Chứng thực hợp đồng cho thuê nhà ở (tại đô thị)',20,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:6.0pt;\r\nmargin-left:0in;text-align:justify;line-height:15.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Trình tự thực hiện</span></b><span lang=\"NL\">:<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 1:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span>Tổ chức, cá nhân yêu\r\ncầu<span class=\"apple-converted-space\">&nbsp;</span><span lang=\"NL\">chứng thực</span><span class=\"apple-converted-space\">&nbsp;</span>nộp hồ sơ tại<span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Bộ phận tiếp nhận và trả kết quả - UBND cấp huyện. Cán bộ kiểm tra và tiếp\r\nnhận hồ sơ,</span><span class=\"apple-converted-space\">&nbsp;</span>ghi vào sổ,<span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">công dân nộp lệ phí chứng thực.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Thời gian nhận hồ sơ: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 2:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span><span lang=\"NL\" style=\"letter-spacing: -0.3pt;\">Người yêu\r\ncầu chứng thực phải ký trước mặt người thực hiện chứng thực (</span></span>ký tắt vào từng trang\r\ncủa hợp đồng, trừ trang cuối phải ký đầy đủ).<o:p></o:p><u1:p></u1:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 3:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Người thực hiện chứng thực phải ghi rõ\r\nl</span>ời\r\nchứng với các nội dung: thời điểm giao kết hợp đồng, địa điểm công chứng, chứng\r\nthực, năng lực hành vi dân sự, chữ ký của các bên và nội dung thoả thuận của\r\ncác bên<span lang=\"NL\">, sau đó ký và ghi rõ họ, tên và đóng\r\ndấu của cơ quan có thẩm quyền chứng thực.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 4:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Tổ chức, cá nhân nhận kết quả tại Bộ\r\nphận tiếp nhận và trả kết quả - UBND cấp huyện.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Thời gian trả kết quả: Trong giờ hành chính từ thứ 2 đến thứ 6 hàng tuần.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Cách thức thực hiện:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">Bộ phận tiếp nhận và trả kết\r\nquả - UBND cấp huyện</span><i><span style=\"letter-spacing: -0.3pt;\">.</span></i><u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Thành phần, số lượng hồ sơ</span></b><b><u1:p></u1:p></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 5.65pt 6pt -3.1pt; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\">&nbsp;a) Thành phần hồ sơ:<u1:p></u1:p></span></i></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">1-<span class=\"apple-converted-space\">&nbsp;</span></span>Phiếu yêu cầu chứng\r\nthực hợp đồng, văn bản<span lang=\"NL\">;<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">2-<span class=\"apple-converted-space\">&nbsp;</span></span>Hợp đồng cho thuê nhà\r\nở (có đầy đủ các điều khoản theo quy định pháp luật);<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">3- Bản chính Giấy\r\nchứng nhận quyền sở hữu nhà ở hoặc Giấy chứng nhận quyền sử dụng đất gắn liền\r\nvới nhà ở (để đối chiếu);<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">4- Giấy tờ tùy thân\r\ncủa các bên thuê và cho thuê (để đối chiếu).<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\">b) Số bộ hồ sơ:</span></i></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">03 bộ.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Thời hạn giải quyết:<u1:p></u1:p></span></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">Việc tiếp nhận yêu cầu chứng thực chữ ký trong thời gian làm việc buổi sáng\r\nhoặc buổi chiều thì phải được thực hiện chứng thực ngay trong buổi làm việc đó;\r\ntrường hợp cần phải xác minh làm rõ nhân thân của người yêu cầu chứng thực thì\r\nthời hạn trên được kéo dài thêm nhưng không được quá 03 ngày làm việc.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Đối tượng thực hiện:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">Tổ chức, cá nhân<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Cơ quan thực\r\nhiện:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></b><b><u1:p></u1:p></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan có thẩm quyền quyết định: Phòng Tư pháp cấp huyện.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện TTHC: Phòng Tư pháp cấp huyện.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Kết quả:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">Văn bản chứng thực.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\"></span></b><span class=\"apple-converted-space\"></span><b><span lang=\"NL\">Phí, lệ phí:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">Lệ phí chứng thực:<span class=\"apple-converted-space\">&nbsp;</span>Tính trên\r\ntổng số tiền thuê tùy từng trường hợp.<u1:p></u1:p></span><o:p></o:p></p><table class=\"MsoNormalTable\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"612\" style=\"width: 459pt; margin-left: 5.4pt;\">\r\n 