insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (127,'KHH22A','Thu hồi Giấy chứng nhận đăng ký hợp tác xã (Đối với trường hợp hợp tác xã giải thể tự nguyện)',15,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">33.1.\r\nTrình tự thực hiện: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n1:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> Cá nhân<span style=\"letter-spacing:-.2pt\"> nộp hồ sơ tại Bộ phận tiếp nhận và trả kết quả\r\ncủa uBND huyện; cán bộ tiếp nhận hướng dẫn viết giấy đề nghị đăng ký kinh\r\ndoanh, kiểm tra hồ sơ và viết giấy hẹn.</span><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n2:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; Hồ sơ được chuyển tới phòng Tài chính - Kế\r\nhoạch để thẩm định và hoàn thiện giấy chứng nhận đăng ký kinh doanh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.3pt\">Bước 3:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.3pt\"> Phòng tài chính - Kế hoạch trình lãnh đạo UBND huyện xem\r\nxét và ký giấy chứng nhận đăng ký kinh doanh; Hồ sơ được chuyển lại một cửa của\r\nUBND huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n4:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> Cá nhân xuất\r\ntrình giấy hẹn trả kết quả, nộp lệ phí và lấy Giấy CN kinh doanh tại bộ phận tiếp\r\nnhận và trả kết quả của UBND&nbsp; huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">33.2.\r\nCách thức thực hiện: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tại Bộ phận tiếp nhận và trả kết\r\nquả của UBND cấp huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">33.3.\r\nHồ sơ:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a)\r\nThành phần hồ sơ bao gồm:<o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;layout-grid-mode:line\">1. Đơn xin\r\ngiải thể <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;layout-grid-mode:line\">2. Nghị\r\nquyết Đại hội xã viên về việc giải thể hợp tác xã; <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-autospace:none\"><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">b)\r\nSố lượng hồ sơ:</span></i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; 01(bộ)<i><o:p></o:p></i></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">33.4.\r\nThời hạn giải quyết:&nbsp; </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">05 ngày làm việc kể từ ngày nhận\r\nđủ hồ sơ hợp lệ<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">33.5.\r\nCơ quan thực hiện:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1-\r\nCơ quan có thẩm quyền quyết định theo quy định: Ủy ban nhân dân cấp huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2- Cơ quan trực tiếp thực hiện:\r\nPhòng Tài chính - Kế hoạch<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">33.6.\r\nĐối tượng thực hiện:&nbsp; </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tổ chức&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;