insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (170,'LDX32Z','Hưởng trợ hàng tháng đối với Anh hùng lực lượng vũ trang nhân dân (AHLLVTND), Anh hùng Lao động trong thời kỳ kháng chiến (AHLĐTTKKC).',16,NULL,2,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;margin-right:0in;margin-bottom:7.5pt;\r\nmargin-left:0in;line-height:15.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Trình tự thực hiện.<u1:p></u1:p></span></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\">Bước 1:<span class=\"apple-converted-space\">&nbsp;</span></span></b><span lang=\"NL\">UBND cấp xã chuyển hồ sơ theo quy định đến Phòng Lao động- Thương binh và\r\nXã hội huyện, thành phố.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><b><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">Bước 2</span></b><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">: Phòng Lao động - Thương binh và Xã hội trong thời\r\ngian 05 ngày kể từ ngày nhận đủ các giấy tờ,&nbsp;<span class=\"apple-converted-space\">&nbsp;</span>có trách nhiệm lập danh sách, gửi hồ\r\nsơ đến Sở Lao động – Thương binh và Xã hội.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Cách thức thực hiện:</span></b><u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">Trực tiếp tại Bộ phận\r\nTiếp nhận và trả kết quả- UBND huyện<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Thành phần, số lượng hồ\r\nsơ.<u1:p></u1:p></span></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\">a) Thành phần hồ sơ bao gồm:<u1:p></u1:p></span></i></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\">Thành phần hồ sơ bao gồm:<u1:p></u1:p></span></i></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">1. Bản khai cá nhân (<a href=\"http://113.190.246.170/hcc-bacgiang-dsdv/admin/record#BM1\">Mẫu<span class=\"apple-converted-space\"><span style=\"color: windowtext;\">&nbsp;</span>AH1</span></a>).<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">2. Bản sao Quyết định\r\nphong tặng danh hiệu Anh hùng hoặc bản sao Bằng Anh hùng.<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><b><i><span lang=\"NL\">b) Số lượng hồ sơ:</span></i></b><span lang=\"NL\">&nbsp;<span class=\"apple-converted-space\">&nbsp;</span>01 (bộ).<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">Thời hạn giải quyết:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">&nbsp;</span></span><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">05 ngày kể từ ngày nhận đủ hồ\r\nsơ.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Đối tượng thực hiện:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Tổ chức.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Cơ quan thực hiện.<u1:p></u1:p></span></b><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan trực tiếp thực hiện: Phòng Lao động - Thương binh và xã hội.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Cơ quan phối hợp: Ủy ban nhân dân cấp xã.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Kết quả:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Danh sách tổng hợp.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Phí, lệ phí:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Không.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Tên mẫu đơn, mẫu tờ khai:</span></b><u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\">1. Bản khai cá nhân (<a href=\"http://113.190.246.170/hcc-bacgiang-dsdv/admin/record#BM1\">Mẫu<span class=\"apple-converted-space\"><span style=\"color: windowtext;\">&nbsp;</span>AH1</span></a>).<u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><i><span lang=\"NL\" style=\"letter-spacing: -0.7pt;\">&nbsp;(Thông\r\ntư số 05/2013/TT-BLĐTBXH ngày 15 tháng 5 năm 2013)</span></i><u1:p></u1:p><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Yêu cầu, điều kiện:</span></b><span class=\"apple-converted-space\"><span lang=\"NL\">&nbsp;</span></span><span lang=\"NL\">Không.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; line-height: 15pt;\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span lang=\"NL\">Căn cứ pháp lý.<u1:p></u1:p></span></b><o:p></o:p></p><h1 style=\"margin: 6pt 0in 0.0001pt -1.5pt; text-align: justify;\"><b><span lang=\"NL\" style=\"font-size: 12pt;\">- Pháp lệnh số 04/2012/UBTVQH13 ngày 16 tháng\r\n7 năm 2012 của Ủy ban thường vụ Quốc hội sửa đổi, bổ sung một số điều của Pháp\r\nlệnh ưu đãi người có công với cách mạng.<u1:p></u1:p></span></b><span style=\"font-size: 12pt;\"><o:p></o:p></span></h1><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; text-align: justify; line-height: 15pt;\"><span lang=\"NL\">- Nghị định số 31/2013/NĐ-CP ngày 09/4/2013 của Chính phủ quy định chi\r\ntiết, hướng dẫn thi hành một số điều của Pháp lệnh Ưu đãi người có công với\r\ncách mạng.<u1:p></u1:p></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-pagination:none\">\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin: 6pt 0in 7.5pt; line-height: 15pt;\"><span lang=\"NL\">- Thông tư số 05/2013/TT-BLĐTBXH ngày 15/5/2013\r\ncủa Bộ Lao động – Thương binh và Xã hội hướng dẫn về thủ tục lập hồ sơ, quản lý\r\nhồ sơ, thực hiện chế độ ưu đãi người có công với cách mạng và thân nhân.</span><o:p></o:p></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (171,'KHH32A','Tạm ngừng hoạt động hợp tác xã',15,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trình tự thực hiện: <o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n1:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> Cá nhân<span style=\"letter-spacing:-.2pt\"> nộp hồ sơ tại Bộ phận tiếp nhận và trả kết quả\r\ncủa UBND huyện; cán bộ tiếp nhận hướng dẫn viết giấy đề nghị đăng ký kinh\r\ndoanh, kiểm tra hồ sơ và viết giấy hẹn.</span><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n2:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; Hồ sơ được chuyển tới phòng Tài chính - Kế\r\nhoạch để thẩm định và hoàn thiện giấy chứng nhận đăng ký kinh doanh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.3pt\">Bước 3:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.3pt\"> Phòng tài chính - Kế hoạch trình lãnh đạo UBND huyện xem\r\nxét và ký giấy chứng nhận đăng ký kinh doanh; Hồ sơ được chuyển lại một cửa của\r\nUBND huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bước\r\n4:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> Cá nhân xuất\r\ntrình giấy hẹn trả kết quả, nộp lệ phí và lấy Giấy CN kinh doanh tại bộ phận\r\ntiếp nhận và trả kết quả của UBND&nbsp; huyện.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Cách thức thực hiện:&nbsp; </span></b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tại Bộ phận tiếp nhận và trả kết\r\nquả của UBND cấp huyện<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\">&nbsp;&nbsp;&nbsp;&nbsp;<b><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Hồ sơ:<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\"><i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a)\r\nThành phần hồ sơ bao gồm:<o:p></o:p></span></i></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1-\r\nThông báo tạm ngừng hoạt động hợp tác xã<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2-\r\nQuyết định của Ban quản trị<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">3-\r\nBiên bản/ nghị quyết Đại hội xã viên&nbsp; <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-pagination:\r\nnone;tab-stops:1.5in;mso-layout-grid-align:none;text-autospace:none\">