insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (235,'LDX33Z','Hưởng trợ một lần đối với thân nhân hoặc người thờ cúng Anh hùng lực lượng vũ trang nhân dân (AHLLVTND), Anh hùng Lao động trong thời kỳ kháng chiến (AHLĐTTKKC).',16,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-pagination:none\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">41.1. Trình tự thực hiện.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">Bước 1: </span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">UBND cấp xã chuyển hồ sơ theo quy định đến Phòng Lao\r\nđộng- Thương binh và Xã hội huyện, thành phố.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.2pt;mso-ansi-language:NL\">Bước 2</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;letter-spacing:-.2pt;mso-ansi-language:\r\nNL\">: Phòng Lao động - Thương binh và Xã hội trong thời gian 05 ngày kể từ\r\nngày&nbsp; nhận đủ các giấy tờ, có trách nhiệm\r\nlập danh sách gửi hồ sơ đến Sở Lao động – Thương binh và Xã hội.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">41.2. Cách thức thực hiện:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trực tiếp tại Bộ phận Tiếp nhận\r\nvà trả kết quả- UBND huyện<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">41.3. Thành phần, số lượng hồ sơ.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">a)\r\nThành phần hồ sơ bao gồm: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">Thành\r\nphần hồ sơ bao gồm: <o:p></o:p></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-layout-grid-align:none;\r\ntext-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1. Bản\r\nkhai cá nhân (<a href=\"#BM1\"><u><span style=\"color:windowtext;text-underline:blue;text-decoration:none;text-underline:\r\nnone\">Mẫu</span></u><u><span style=\"color:windowtext;\r\ntext-underline:blue;text-decoration:none;text-underline:none\"> AH2</span></u></a>)\r\n<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-layout-grid-align:none;\r\ntext-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.\r\nBiên bản ủy quyền (<a href=\"#UQ1\"><u><span style=\"color:windowtext;text-underline:blue;text-decoration:none;text-underline:\r\nnone\">Mẫu</span></u><u><span style=\"color:windowtext;\r\ntext-underline:blue;text-decoration:none;text-underline:none\"> UQ</span></u></a>).<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.\r\nBản sao Quyết định truy tặng danh hiệu Anh hùng hoặc bản sao Bằng Anh hùng.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">b)\r\nSố lượng hồ sơ:</span></i></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">&nbsp; 01 (bộ).<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.2pt;mso-ansi-language:NL\">41.4. Thời hạn giải quyết:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;letter-spacing:-.2pt;\r\nmso-ansi-language:NL\"> 05 ngày kể từ ngày nhận đủ hồ sơ.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">41.5. Đối tượng thực hiện:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> Tổ chức.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">41.6. Cơ quan thực hiện.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">- Cơ quan\r\ntrực tiếp thực hiện: Phòng Lao động - Thương binh và xã hội.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">- Cơ quan\r\nphối hợp: Ủy ban nhân dân cấp xã.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">41.7. Kết quả:</span></b><span lang=\"NL\" style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> Danh sách tổng hợp.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nmso-ansi-language:NL\">41.8. Phí, lệ phí:</span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> Không.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nNL\">41.9. Tên mẫu đơn, mẫu tờ khai:</span></b><span lang=\"NL\" style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\"> <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-layout-grid-align:none;\r\ntext-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1. Bản\r\nkhai cá nhân (<a href=\"#BM1\"><u><span style=\"color:windowtext;text-underline:blue;text-decoration:none;text-underline:\r\nnone\">Mẫu</span></u><u><span style=\"color:windowtext;\r\ntext-underline:blue;text-decoration:none;text-underline:none\"> AH2</span></u></a>)\r\n<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-layout-grid-align:none;\r\ntext-autospace:none\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.\r\nBiên bản ủy quyền (<a href=\"#UQ1\"><u><span style=\"color:windowtext;text-underline:blue;text-decoration:none;text-underline:\r\nnone\">Mẫu</span></u><u><span style=\"color:windowtext;\r\ntext-underline:blue;text-decoration:none;text-underline:none\"> UQ</span></u></a>).<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\"><i><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.7pt;mso-ansi-language:NL\">&nbsp;(theo Thông tư số 05/2013/TT-BLĐTBXH ngày 15\r\ntháng 5 năm 2013)</span></i><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;\r\nletter-spacing:-.7pt\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nNL;mso-bidi-font-style:italic\">41.10. Yêu cầu, điều kiện: </span></b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL;\r\nmso-bidi-font-style:italic\">Không.<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><b><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nNL;mso-bidi-font-style:italic\">41.11. Căn cứ pháp lý.<o:p></o:p></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><span lang=\"NL\" style=\"font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">- Pháp lệnh số 04/2012/UBTVQH13\r\nngày 16 tháng 7 năm 2012 của Ủy ban thường vụ Quốc hội sửa đổi, bổ sung một số\r\nđiều của Pháp lệnh ưu đãi người có công với cách mạng.<b><o:p></o:p></b></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">- Nghị định\r\nsố 31/2013/NĐ-CP ngày 09/4/2013 của Chính phủ quy định chi tiết, hướng dẫn thi\r\nhành một số điều của Pháp lệnh Ưu đãi người có công với cách mạng.<o:p></o:p></span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\" style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:NL\">- Thông tư\r\nsố 05/2013/TT-BLĐTBXH ngày 15/5/2013 của Bộ Lao động – Thương binh và Xã hội\r\nhướng dẫn về thủ tục lập hồ sơ, quản lý hồ sơ, thực hiện chế độ ưu đãi người có\r\ncông với cách mạng và thân nhân.<o:p></o:p></span></p>',NULL,'',1);
insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (236,'LDX35Z','Xác nhận và hưởng chế độ ưu đãi đối với con đẻ của người hoạt động kháng chiến bị nhiễm chất độc hóa học.',16,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-pagination:none\"><b><span lang=\"NL\"><font face=\"Times New Roman\">43.1. Trình tự thực hiện.<o:p></o:p></font></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\"><b><span lang=\"NL\">Bước 1: </span></b><span lang=\"NL\">UBND cấp xã chuyển hồ sơ theo quy định đến Phòng Lao\r\nđộng- Thương binh và Xã hội.<o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\"><b><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">Bước 2:&nbsp;&nbsp; </span></b><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">Phòng Lao\r\nđộng - Thương binh và Xã </span><span lang=\"NL\" style=\"font-size: 16pt; letter-spacing: -0.2pt;\">hội trong thời gian 10 ngày&nbsp; </span><span lang=\"NL\">kể từ ngày nhận đủ các giấy tờ hợp lệ, có trách nhiệm\r\nkiểm tra, lập danh sách người đủ điều kiện kèm giấy tờ quy định gửi Sở Lao động\r\n- Thương binh và Xã hội<span style=\"letter-spacing:-.2pt\"><o:p></o:p></span></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\"><b><span lang=\"NL\">43.2. Cách thức thực hiện:</span></b><span lang=\"NL\"> <o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\">Trực tiếp tại Bộ phận Tiếp nhận\r\nvà trả kết quả- UBND huyện<o:p></o:p></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\"><font face=\"Times New Roman\">43.3. Thành phần, số lượng hồ sơ.<o:p></o:p></font></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><i><span lang=\"NL\"><font face=\"Times New Roman\">a)\r\nThành phần hồ sơ bao gồm: <o:p></o:p></font></span></i></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-layout-grid-align:none;\r\ntext-autospace:none\"><font face=\"Times New Roman\">1<span lang=\"NL\">.\r\nBản khai cá nhân.( Mẫu HH 1)<o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-layout-grid-align:none;\r\ntext-autospace:none\"><span lang=\"NL\"><font face=\"Times New Roman\">2. Bản sao giấy khai sinh.<o:p></o:p></font></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\"><font face=\"Times New Roman\">3.\r\nMột trong những giấy tờ của cha đẻ hoặc mẹ đẻ <span lang=\"NL\">chứng minh\r\nthời gian tham gia hoạt động kháng chiến tại vùng mà quân đội Mỹ sử dụng chất\r\nđộc hóa học: Quyết định phục viên, xuất ngũ; giấy X Y Z; giấy chuyển thương,\r\nchuyển viện, giấy điều trị; giấy tờ khác chứng minh có tham gia hoạt động kháng\r\nchiến tại vùng mà quân đội Mỹ sử dụng chất độc hóa học được xác lập từ ngày 30\r\ntháng 4 năm 1975 trở về trước.</span><o:p></o:p></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;mso-layout-grid-align:none;\r\ntext-autospace:none\"><span lang=\"NL\"><font face=\"Times New Roman\">Bản sao: Lý lịch cán bộ, lý lịch đảng viên, lý lịch quân\r\nnhân, Huân chương, Huy chương chiến sĩ giải phóng.<o:p></o:p></font></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\"><b><i><span lang=\"NL\">b)\r\nSố lượng hồ sơ:</span></i></b><span lang=\"NL\">&nbsp; 01 (bộ)<o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\"><b><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">43.4. Thời hạn giải quyết:</span></b><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\"> 10 ngày<o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\"><b><span lang=\"NL\">43.5. Đối tượng thực hiện:</span></b><span lang=\"NL\"> Tổ chức.<o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"NL\"><font face=\"Times New Roman\">43.6. Cơ quan thực hiện.<o:p></o:p></font></span></b></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\"><font face=\"Times New Roman\">- Cơ quan\r\ntrực tiếp thực hiện: Phòng Lao động - Thương binh và xã hội.<o:p></o:p></font></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"NL\"><font face=\"Times New Roman\">- Cơ quan\r\nphối hợp: Ủy ban nhân dân cấp xã.<o:p></o:p></font></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\"><b><span lang=\"NL\">43.7. Kết quả:</span></b><span lang=\"NL\"> Danh sách tổng hợp.<o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font face=\"Times New Roman\"><b><span lang=\"NL\">43.8. Phí, lệ phí:</span></b><span lang=\"NL\"> Không.<o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><font face=\"Times New Roman\"><b><span lang=\"NL\">43.9. Tên mẫu đơn, mẫu tờ khai:</span></b><span lang=\"NL\"> <o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><font face=\"Times New Roman\"><span lang=\"NL\">&nbsp;-Bản khai cá nhân ( Mẫu HH1 )</span><b><o:p></o:p></b></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify;mso-layout-grid-align:\r\nnone;text-autospace:none\"><font face=\"Times New Roman\"><i><span lang=\"NL\" style=\"letter-spacing: -0.3pt;\">&nbsp;(theo Thông tư số 05/2013/TT-BLĐTBXH ngày 15\r\n/5/2013)</span></i><o:p></o:p></font></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt\"><font face=\"Times New Roman\"><b><span lang=\"NL\">43.10. Yêu cầu, điều kiện:</span></b><