insert  into `cores_record_type`(`pk`,`c_code`,`c_name`,`fk_linh_vuc`,`fk_member`,`c_scope`,`c_content`,`c_xml_data`,`c_order`,`c_status`) values (237,'LDX36Z','Xác nhận và hưởng chế độ ưu đãi đối với con đẻ của người hoạt động kháng chiến bị nhiễm chất độc hóa học.',16,NULL,3,'<p class=\"MsoNormal\" style=\"margin-top: 6pt;\"><span style=\"font-weight: 700;\"><span lang=\"NL\"><font face=\"Times New Roman\">43.1. Trình tự thực hiện.<o:p></o:p></font></span></span></p><p class=\"MsoNormal\" style=\"margin-top: 6pt; text-align: justify;\"><font face=\"Times New Roman\"><span style=\"font-weight: 700;\"><span lang=\"NL\">Bước 1:&nbsp;</span></span><span lang=\"NL\">UBND cấp xã chuyển hồ sơ theo quy định đến Phòng Lao động- Thương binh và Xã hội.<o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top: 6pt; text-align: justify;\"><font face=\"Times New Roman\"><span style=\"font-weight: 700;\"><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">Bước 2:&nbsp;&nbsp;&nbsp;</span></span><span lang=\"NL\" style=\"letter-spacing: -0.2pt;\">Phòng Lao động - Thương binh và Xã&nbsp;</span><span lang=\"NL\" style=\"font-size: 16pt; letter-spacing: -0.2pt;\">hội trong thời gian 10 ngày&nbsp;&nbsp;</span><span lang=\"NL\">kể từ ngày nhận đủ các giấy tờ hợp lệ, có trách nhiệm kiểm tra, lập danh sách người đủ điều kiện kèm giấy tờ quy định gửi Sở Lao động - Thương binh và Xã hội<span style=\"letter-spacing: -0.2pt;\"><o:p></o:p></span></span></font></p><p class=\"MsoNormal\" style=\"margin-top: 6pt; text-align: justify;\"><font face=\"Times New Roman\"><span style=\"font-weight: 700;\"><span lang=\"NL\">43.2. Cách thức thực hiện:</span></span><span lang=\"NL\"><o:p></o:p></span></font></p><p class=\"MsoNormal\" style=\"margin-top: 6pt; text-align: justify;\"><font face=\"Times New Roman\">Trực tiếp tại Bộ phận Tiếp nhận và trả kết quả- UBND huyện<o:p></o:p></font></p><p class=\"MsoNormal\" style=\"margin-top: 6pt; text-align: justify;\"><span style=\"font-weight: 700;\